// n8n Code node (JavaScript)

// Input: JSON array in items[0].json
// Example: your provided JSON pasted into items[0].json.sections
// Output: single Markdown string in items[0].json.markdown

const sections = $input.first().json.outline;  

function toMarkdown(data) {
  return data.map(section => {
    return `## ${section.section_title}\n\n**${section.section_question}**\n\n${section.answer}\n`;
  }).join("\n");
}

return [
  {
    json: {
      markdown: toMarkdown(sections)
    }
  }
];
