const results = $input.all();

let html = `<h2>üìù Plagiarism Report</h2>`;
html += `<p>The following content has been analyzed. Below is the similarity report:</p>`;

for (const item of results) {
  const phrase = item.json.phrase;
  const matches = item.json.results;

  html += `<hr/>`;
  html += `<p><strong>Original Phrase:</strong><br>"${phrase}"</p>`;

  if (!matches || matches.length === 0) {
    html += `<p style="color:green;">‚úÖ No matches found</p>`;
    continue;
  }

  html += `<ul>`;
  for (const match of matches) {
    const title = match.title;
    const link = match.link;
    const score = match.scores?.[0]?.score?.toFixed(2);
    const matchedSentence = match.scores?.[0]?.sentence;

    html += `
      <li>
        <p><strong>Matched Source:</strong> <a href="${link}" target="_blank">${title}</a></p>
        <p><strong>Similarity Score:</strong> ${score}</p>
        <p><em>"${matchedSentence}"</em></p>
      </li>
    `;
  }
  html += `</ul>`;
}

return [
  {
    json: {
      subject: "üìÑ Plagiarism Report",
      body: html
    }
  }
];
