/**
 * Buckets:
 *  - Keywords  (keywords/keyword, cluster, intent, priority, volume_sum?, difficulty_avg?)
 *  - Published (Datetime, Keyword, Cluster, Title, Slug/Handle, URL, Status)
 *
 * Output:
 * {
 *   keywords: [{ keyword, cluster, intent, priority, volume_sum?, difficulty_avg?, slug }],
 *   published: {
 *     slugs: string[],
 *     clusters: string[],
 *     slugToUrl: { [slug]: url },
 *     pairs: string[]   // "keyword||cluster" normalized
 *   }
 * }
 */

const toAscii = (s='') =>
  String(s).normalize('NFD').replace(/[\u0300-\u036f]/g,'').replace(/ñ/gi,'n');

const toSlug = (s='') => toAscii(String(s).toLowerCase())
  .replace(/[^a-z0-9\s-]/g,'')
  .trim().replace(/\s+/g,'-').replace(/-+/g,'-')
  .slice(0,65).replace(/-+$/,'');

const isHttp = (s='') => /^https?:\/\//i.test(String(s||'').trim());

const lcKeys = (obj={}) => {
  const out = {};
  for (const [k,v] of Object.entries(obj)) out[String(k).toLowerCase()] = v;
  return out;
};

// normalized pair helpers
const norm = (s='') =>
  toAscii(String(s).toLowerCase().trim().replace(/\s+/g,' '));
const pairKey = (k, c) =>
  `${norm(k)}||${norm(c || '(unclustered)')}`;

// Collect all merged rows
const rows = $input.all().map(i => i.json).filter(Boolean);

// Buckets
const kwRows = [];
const pubRows = [];

// Classify rows; explicitly ignore Links rows
for (const r0 of rows) {
  const r = lcKeys(r0);
  if (!Object.values(r).some(v => String(v||'').trim())) continue;

  // IGNORE Links rows (Key + URL [+ Anchor])
  if (r.key && r.url && isHttp(r.url)) { continue; }

  // Published: has a slug (or handle) and either URL or Title or Keyword
  if ((r.slug || r.handle) && (r.url || r.title || r.keyword || r.keywords)) { pubRows.push(r); continue; }

  // Keywords: has 'keywords' or 'keyword' column
  if (r.keywords || r.keyword) { kwRows.push(r); continue; }

  // Fallbacks (conservative): ignore rows that only have URL
  if (isHttp(r.url)) { continue; }
}

// --- Normalize Keywords ---
const seen = new Set(); // de-dupe identical keyword->slug rows
const keywords = [];
for (const r of kwRows) {
  const keyword = String(r.keywords ?? r.keyword ?? '').trim();
  if (!keyword) continue;

  const clusterRaw = String(r.cluster ?? '').trim();
  const cluster    = clusterRaw || '(unclustered)';
  const intent     = String(r.intent ?? 'informational').trim();

  const pRaw       = String(r.priority ?? r.prioridad ?? '3').trim();
  const pNum       = parseInt(pRaw, 10);
  const priority   = Math.min(5, Math.max(1, Number.isFinite(pNum) ? pNum : 3));

  const volNum     = Number(r.volume_sum ?? r.volume);
  const kdNum      = Number(r.difficulty_avg ?? r.difficulty ?? r.kd);
  const volume_sum     = Number.isFinite(volNum) ? volNum : undefined;
  const difficulty_avg = Number.isFinite(kdNum) ? kdNum : undefined;

  const slug = toSlug(keyword);
  if (!slug || seen.has(slug)) continue;
  seen.add(slug);

  keywords.push({ keyword, cluster, intent, priority, volume_sum, difficulty_avg, slug });
}

// --- Normalize Published ---
const slugs = new Set();
const clusters = new Set();
const slugToUrl = {};
const pairs = new Set();

for (const r of pubRows) {
  const slug = String((r.slug ?? r.handle ?? '')).trim().toLowerCase();
  if (slug) {
    slugs.add(slug);
    if (isHttp(r.url)) slugToUrl[slug] = String(r.url).trim();
  }

  const clCell = String(r.cluster ?? '').trim();
  if (clCell) clusters.add(toSlug(clCell));

  // Build (keyword, cluster) pair if the Published sheet includes them
  const kwCell = String((r.keyword ?? r.keywords ?? '')).trim();
  if (kwCell) {
    pairs.add(pairKey(kwCell, clCell || '(unclustered)'));
  }
}

// Emit consolidated item (no Links)
return [{
  json: {
    keywords,
    published: {
      slugs: Array.from(slugs),
      clusters: Array.from(clusters),
      slugToUrl,
      pairs: Array.from(pairs)
    }
  }
}];

const data = $input.first().json;
const cfg  = ($items('Set - Config')[0] || { json: {} }).json;

// settings
const maxPerRun = Math.max(1, parseInt(cfg.maxPerRun ?? '1', 10));
const onePerCluster = true;
const avoidPublishedClusters = false;

// helpers
const num = (v,d=0)=>{ const n=Number(v); return Number.isFinite(n)?n:d; };
const toAscii = s=>String(s||'').normalize('NFD').replace(/[\u0300-\u036f]/g,'').replace(/ñ/gi,'n');
const norm = s=>toAscii(String(s).toLowerCase().trim().replace(/\s+/g,' '));
const pairKey = (k,c)=>`${norm(k)}||${norm(c||'(unclustered)')}`;
const lc = s=>String(s||'').toLowerCase();

// lookups (from Normalize)
const publishedPairs = new Set((data.published?.pairs || []).map(String));
const publishedClusters = new Set((data.published?.clusters || []).map(lc));

// eligible (dedupe by keyword+cluster)
const seenPairs = new Set();
let candidates = [];
for (const k of (data.keywords || [])) {
  if (!k || !k.keyword) continue;
  const pKey = pairKey(k.keyword, k.cluster);
  if (publishedPairs.has(pKey)) continue;
  if (seenPairs.has(pKey)) continue;
  seenPairs.add(pKey);
  candidates.push(k);
}

// optional: avoid clusters that already have content
if (avoidPublishedClusters) {
  candidates = candidates.filter(k => !publishedClusters.has(lc(k.cluster)));
}

// sort: priority (5 best) → volume desc → difficulty asc → keyword A→Z
candidates.sort((a,b)=>{
  const pa=num(a.priority,3), pb=num(b.priority,3);
  if (pa!==pb) return pb-pa;
  const va=num(a.volume_sum??a.volume,0), vb=num(b.volume_sum??b.volume,0);
  if (va!==vb) return vb-va;
  const da=num(a.difficulty_avg??a.difficulty??a.kd,999), db=num(b.difficulty_avg??b.difficulty??b.kd,999);
  if (da!==db) return da-db;
  return String(a.keyword||'').localeCompare(String(b.keyword||''));
});

// pick up to maxPerRun, one-per-cluster if enabled
const picks=[], seenClusters=new Set();
for (const k of candidates) {
  if (picks.length>=maxPerRun) break;
  const ck = lc(k.cluster||'(unclustered)');
  if (onePerCluster && seenClusters.has(ck)) continue;
  picks.push(k); seenClusters.add(ck);
}

if (!picks.length) return [{ json:{ skip:true, reason:'No eligible keywords after (keyword,cluster) dedupe.' }}];
return picks.map(p=>({ json:p }));

