// 1) Get prior state from the SAME item (if present)
const list = Array.isArray($json.existingSlugs) ? $json.existingSlugs.slice() : [];
const seen = new Set(list.map(s => String(s).toLowerCase()));

// 2) Read Shopify response from body.*
const edges    = $json.body?.data?.node?.articles?.edges || [];
const pageInfo = $json.body?.data?.node?.articles?.pageInfo || {};

// 3) Accumulate unique handles
for (const e of edges) {
  const h = String(e?.node?.handle || '').trim().toLowerCase();
  if (h && !seen.has(h)) {
    seen.add(h);
    list.push(h);
  }
}

// 4) Emit updated state for the loop
return [{
  json: {
    existingSlugs: list,
    __cursor: pageInfo?.hasNextPage ? pageInfo?.endCursor : null,
    __hasNext: !!pageInfo?.hasNextPage,
    countAccumulated: list.length
  }
}];

const all = $input.all();

// 1) Existing slugs
const existingSlugs = (all.find(i => i.json.existingSlugs) || { json: { existingSlugs: [] } }).json.existingSlugs;

// 2) Keyword data
const kwItem = all.find(i => i.json.keyword) || { json: {} };
const kw = kwItem.json.keyword || '';
const cluster = kwItem.json.cluster || '';
const intent = kwItem.json.intent || '';

// 3) Candidate internal links
const links = (all.find(i => i.json.links) || { json: { links: [] } }).json.links || [];

// --- PROMPT (system + user) ---
const system = `You are a team composed of a senior editor (English, en-EN), and an SEO/AEO expert.
You write for humans (E-E-A-T): clear and useful, without fluff or exaggeration.
Do not use first person or testimonials. Use a neutral, respectful, and didactic tone.
Use sentence case in ALL content: titles (title, seo_title, H1, H2, H3), paragraphs, and image alt text, with capitalization only at the beginning of the sentence and for proper nouns or acronyms (e.g., SEO, AEO, HTML).
Do not use Title Case or ALL CAPS for common words.
Do not use semicolons (;) or double dashes (--). Replace them with a period or comma as appropriate.`;

// --- NEW BLOCK: internal link policy ---
const internalLinkPolicy = `
### Internal linking (evaluation and use)
- I give you a list of internal links with their associated keywords:
${JSON.stringify(links, null, 2)}
- Goal: **insert exactly 1 (one) internal link** in the article **only if** it is relevant.
- Minimum relevance: there is a clear semantic match between the target keyword ("${kw}"), the cluster ("${cluster}"), the article content or its sections, and the keywords associated with the link.
- Selection preferences:
  - Direct keyword match > partial match > thematic relation.
- Insertion:
  - Place the link **inside an existing paragraph**, naturally (not at the beginning or end of the article).
  - Use a **descriptive and natural anchor** (not "click here").
  - Insert **only that link** with the tag <a href="...">…</a>. Do not add more links or invent URLs.
  - Do not repeat the same URL more than once.
- If **no link is clearly relevant**, **do not insert any**.
`;

// --- Your original user prompt + additions ---
const user = `
Write **ONE** SEO- and AEO-optimized article about the keyword: "${kw}".
Cluster: ${cluster}. Search intent: ${intent}. Language: English (en-EN).
Avoid repeating slugs already used in the blog: [${existingSlugs.join(', ')}].

${internalLinkPolicy}

Return ONLY JSON in this exact shape:
{
  "title": string,
  "slug": string,                   // kebab-case ascii, ≤65, NOT included in [${existingSlugs.slice(0,50).join(', ')}]
  "backup_slugs": string[],         // 2–4 valid and free alternatives
  "seo_title": string,              // ≤70
  "seo_description": string,        // ≤160
  "tags": string[],                 // 3–8
  "content_html": string,           // ONLY: h2,h3,p,ul,ol,li,a,strong,em,blockquote,code,pre
  "resumen": string,                // will appear on the blog home
  "image_prompt": string,           // brief for hero image, without overlay text
  "image_alt": string,              // ≤120 chars
  "internal_link_used": {           // NEW — meta for control
    "url": string | null,           // URL used or null if no relevant link
    "anchor": string | null,        // anchor text used or null
    "reason": string | null         // brief justification of relevance or null
  }
}

### Hard rules (comply with all):
- Start with a **Direct Answer** block (35–60 words) that responds to the user’s intent without digressions.
- **Structure**: H2/H3 by intent (informational: definition → usefulness → how to apply it → common mistakes → FAQs; comparative: criteria → alternatives → pros/cons → FAQs).
- **SEO/AEO**: the keyword appears in the first 100 words and in an H2 (without over-optimizing); use synonyms and related entities naturally.
- **Length**: ≥900 words unless the keyword is clearly short.
- **Lists** scannable; short paragraphs (≤4 lines).
- content_html must not contain <h1>
- **Internal links**:
  - Keep your 3–6 **suggested internal anchors** (anchor text without URL, as already indicated).
  - **Additionally** apply the internal link policy above to insert **exactly 1** <a href="real URL"> inside a paragraph, **only if** there is clear relevance. If not, insert none.
- **Language**: avoid absolutes and unnecessary jargon; briefly define technical terms.
- Sentence case in titles and text.
- Do not use ';' or '--'.

### Validations before returning JSON (do not show them):
- Wordcount ≥900 approx. in body.
- Character limits ok.
- Valid, unique slug not similar to those given.
- Keyword present in the first 100 words and an H2.
- 3–6 suggested internal anchors inside paragraphs (without URL).
- **If 'internal_link_used.url' is not null**:
  - 'content_html' contains **exactly one** occurrence of that URL as <a href="...">…</a>.
  - There is no other different URL.
- **If 'internal_link_used.url' is null**:
  - 'content_html' does not contain any <a href="http".
- Sentence case and no ';' or '--'.
`;

return [{
  json: {
    messages: [
      { role: 'system', content: system },
      { role: 'user', content: user }
    ],
    response_format: { type: 'json_object' }
  }
}];

const existing = new Set($('Merge - Wiring').first().json.existingSlugs.map(s => String(s).toLowerCase()));
const resp = $input.first().json;
let ai;
try { ai = JSON.parse(resp.choices?.[0]?.message?.content || "{}"); } catch { throw new Error("LLM JSON inválido"); }

const keep = /(\/?)(h1|h2|h3|p|ul|ol|li|a|strong|em|blockquote|code|pre)(\s+[^>]*)?>/i;
const clean = (html="") => html.replace(/<[^>]+>/g, t => keep.test(t) ? t : "");

const toAscii = s => s.normalize('NFD').replace(/[\u0300-\u036f]/g,'').replace(/ñ/gi,'n');
const toSlug = s => toAscii(String(s||'').toLowerCase())
  .replace(/[^a-z0-9\s-]/g,'').trim().replace(/\s+/g,'-').replace(/-+/g,'-').slice(0,65).replace(/-+$/,'');
const cut = (s,n)=>{s=String(s||'');return s.length<=n?s:s.slice(0,n-1).replace(/\s+\S*$/,'')+'…';};

const preferSlug = (s, backups=[]) => {
  let cand = toSlug(s);
  if (!existing.has(cand)) return cand;
  for (const b of backups) {
    const bs = toSlug(b);
    if (bs && !existing.has(bs)) return bs;
  }
  // suffix fallback
  let i = 2;
  while (i < 10) {
    const tryS = (cand + '-' + i).slice(0,65);
    if (!existing.has(tryS)) return tryS;
    i++;
  }
  return cand; // last resort
};

const title = cut(ai.title || "", 90);
const slug = preferSlug(ai.slug || ai.title, Array.isArray(ai.backup_slugs) ? ai.backup_slugs : []);
const seo_title = cut(ai.seo_title || title, 70);
const seo_description = cut(ai.seo_description || "", 160);
const summary = ai.resumen;
let html = clean(ai.content_html || "");

// Ensure single H1
if (!/<h1>/i.test(html)) html = `<h1>${title}</h1>` + html;


return [{
  json: {
    title, slug, seo_title, seo_description, summary, 
    tags: Array.isArray(ai.tags) ? ai.tags.slice(0,8) : [],
    content_html: html,
    image_prompt: String(ai.image_prompt || ''),
    image_alt: cut(ai.image_alt || '', 120),
    existingSlugs: Array.from(existing),
    blogHandle: $('Set - Config').first().json.blogHandle
  }
}];

return [
  {
    json: {
      links: items.map(item => ({
        url: item.json.URL,
        keywords: item.json.Keywords.split(",").map(k => k.trim())
      }))
    }
  }
];
