// n8n Code Node to get the 3 latest videos from YouTube data
// This assumes the input is coming from a previous node with the JSON data

// Get the input data from n8n
const items = $input.all();

// Try different ways to access the data based on n8n's structure
let data;
let videos = [];

// Check if the data is directly in the first item
if (items && items.length > 0) {
  const firstItem = items[0].json;

  // If the data is a string, parse it
  if (typeof firstItem === 'string') {
    data = JSON.parse(firstItem);
  } 
  // If it's already an object
  else if (typeof firstItem === 'object') {
    data = firstItem;
  }

  // Try to find videos in different possible locations
  if (data) {
    if (Array.isArray(data) && data[0] && data[0].videos) {
      videos = data[0].videos;
    } else if (data.videos) {
      videos = data.videos;
    } else if (data.data && Array.isArray(data.data) && data.data[0] && data.data[0].videos) {
      videos = data.data[0].videos;
    }
  }
}

// Check if we found videos
if (!videos || videos.length === 0) {
  throw new Error('No videos found in input data. Please check the data structure.');
}

// Filter and sort videos by publishedTime (most recent first)
const sortedVideos = videos
  .filter(video => video.type === 'video' && video.publishedTime)
  .sort((a, b) => {
    const dateA = new Date(a.publishedTime);
    const dateB = new Date(b.publishedTime);
    return dateB - dateA; // Sort in descending order (newest first)
  });

// Get the top 3 most recent videos
const latestThreeVideos = sortedVideos.slice(0, 3).map(video => ({
  id: video.id,
  url: video.url,
  title: video.title,
  publishedTime: video.publishedTime,
  publishedTimeText: video.publishedTimeText
}));

// Return the result for n8n to process
return latestThreeVideos;

// Alternative: If you want to return each video as a separate item for n8n workflow
// return latestThreeVideos.map(video => ({ json: video }));

