const text = $('Create Notes').first().json.text; // El texto original muy largo que quieres dividir
// Funci√≥n para dividir texto en bloques de max 2000 chars
function splitTextIntoBlocks(str, maxSize = 2000) {
  const result = [];
  for (let i = 0; i < str.length; i += maxSize) {
    const chunk = str.substring(i, i + maxSize);
    result.push({chunk});
  }
  return result;
}
