// Original keyword item from the loop
const original = $json;

// Extract and clean the agent output with multiple fallbacks
let agentOutput = {};
try {
  let rawOutput = original.output || "";

  // Security: Handle multiple markdown variations
  let cleanedOutput = rawOutput
    .replace(/```json\s*/g, '')    // Remove ```json
    .replace(/```\s*$/g, '')       // Remove closing ```
    .replace(/^```\s*/g, '')       // Remove opening ``` 
    .replace(/```$/g, '')          // Remove trailing ```
    .trim();

  // Try parsing the cleaned output
  agentOutput = JSON.parse(cleanedOutput);

} catch (parseError) {
  // Security fallback: Create safe structure if parsing fails
  console.log("JSON Parse Error:", parseError.message);

  agentOutput = { 
    title: `Error: ${original["main keyword"] || "Unknown keyword"}`,
    slug: `error-${Date.now()}`,
    metaDescription: "Content generation failed",
    content: "Failed to generate content. Please check agent configuration.",
    "main keyword": original["main keyword"] || "unknown",
    imageUrl: "",
    altText: "Error generating content",
    parseError: parseError.message,
    rawAgentOutput: original.output
  };
}

// Compute word count from the content
const content = agentOutput.content || "";
const wordCount = content.split(/\s+/).filter(Boolean).length;

// Return wrapped in json object to avoid n8n reserved key conflicts
return [{
  json: {
    ...original,          
    ...agentOutput,       
    wordCount,
    status: wordCount >= 600 ? "content_ready" : "needs_expansion",
    generatedAt: new Date().toISOString()
  }
}];

const originalData = $json;
const expandedResponse = $('Expand Content').first().json.message.content;

// Parse the JSON response from the expanded content
let expandedData = {};
try {
  expandedData = JSON.parse(expandedResponse);
} catch (e) {
  expandedData = { 
    error: "Invalid JSON from expansion", 
    raw: expandedResponse,
    content: expandedResponse // fallback to raw content
  };
}

// Calculate word count from the expanded content
const content = expandedData.content || "";
const wordCount = content.split(/\s+/).filter(Boolean).length;

return [{
  json: {
    ...originalData,     // Keep original data (main keyword, etc.)
    ...expandedData,     // Add all expanded fields
    wordCount,
    status: wordCount >= 600 ? "content_ready" : "needs_expansion",
    wasExpanded: true,
    expandedAt: new Date().toISOString()
  }
}];

const error = $json.error || 'Creation failed';
const keyword = $('Convert to HTML').first()?.json?.['main keyword'] || 'Unknown';

return {
  json: {
    keyword: keyword,
    error: error,
    timestamp: new Date().toISOString(),
    status: 'failed'
  }
};

const response = $json;
let imageUrl = '';

try {
  // OpenRouter returns images in the assistant message
  if (response.choices && response.choices.length > 0) {
    const message = response.choices[0].message;

    // Check for images array in the message
    if (message.images && message.images.length > 0) {
      // Extract the base64 data URL from the first image
      imageUrl = message.images[0].image_url.url;
    }
  }

  // Fallback checks
  if (!imageUrl && response.data && response.data.length > 0) {
    imageUrl = response.data[0].url;
  }

} catch (error) {
  console.error('Error processing OpenRouter image response:', error);
}

const originalInputs = $('When Executed by Another Workflow').item.json;

return {
  json: {
    imageUrl: imageUrl,
    imageTitle: originalInputs.imageTitle,
    keyword: originalInputs.keyword,
    imagePrompt: originalInputs.imagePrompt,
    altText: originalInputs.altText || `${originalInputs.keyword} featured image`,
    imageGenerated: !!imageUrl,
    generatedAt: new Date().toISOString(),
    status: imageUrl ? 'success' : 'failed',
    rawResponse: response
  }
};

const originalData = $('Process Image Response').item.json;
const error = $json.error || 'Image generation failed';

console.error(`Image generation failed for: ${originalData.keyword}`);
console.error(`Error: ${error}`);

return {
  json: {
    imageUrl: '',
    imageTitle: originalData.imageTitle,
    keyword: originalData.keyword,
    imagePrompt: originalData.imagePrompt,
    imageGenerated: false,
    status: 'failed',
    error: error,
    generatedAt: new Date().toISOString(),
    message: `Failed to generate image for '${originalData.imageTitle}'`
  }
};

