// Extract base64 from data URL and convert to binary
const input = $input.first().json;
const base64String = input.imageUrl.replace(/^data:image\/\w+;base64,/, '');
const buffer = Buffer.from(base64String, 'base64');

// Pass through other data and add binary
return {
  json: {
    imageTitle: input.imageTitle,
    imageUrl: input.imageUrl,
    altText: input.altText || `${input.keyword} featured image`,  
    keyword: input.keyword,  
    imagePrompt: input.imagePrompt  
  },
  binary: {
    data: await this.helpers.prepareBinaryData(
      buffer,
      `${input.imageTitle || input.keyword}_raw.png`,
      'image/png'
    )
  }
};
