// Configuration
const AllowedUsers = [
  { userId: 8032941945, userName: 'ruslan_elishev_eu', subscriptionType: 'admin' },
  { userId: 111111111, userName: 'john_doe', subscriptionType: 'basic' },
  { userId: 222222222, userName: 'jane_smith', subscriptionType: 'premium' }
];

const Administrators = [
  { userId: 8032941945, userName: 'ruslan_elishev_eu', chatId: 8032941945 },
  { userId: 333333333, userName: 'admin_user', chatId: 333333333 }
];

const ProcessingRequests = [];

try {
  const message = $input.item.json.message || {};
  const userId = (message.from && message.from.id) || null;
  const userName = (message.from && message.from.username) || (message.from && message.from.first_name) || 'Unknown';
  const chatId = (message.chat && message.chat.id) || null;
  const messageText = message.text || '';
  const languageCode = (message.from && message.from.language_code) || 'en';

  const isAuthorized = AllowedUsers.some(user => user.userId === userId);
  const isAdmin = Administrators.some(admin => admin.userId === userId);
  const isProcessingRequest = ProcessingRequests.some(req => req.userId === userId);
  const isRequestCommand = messageText.toLowerCase() === '/request';

  let allowEntry = isAuthorized || isAdmin;
  let blockReason = '';
  let userMessage = '';
  let adminMessage = '';
  let authorizedMessage = '';

  const currentTime = new Date().toISOString();
  const userInfo = AllowedUsers.find(user => user.userId === userId) || { subscriptionType: 'none' };

  if (allowEntry) {
    authorizedMessage = '‚úÖ <b>Authorization Successful</b>\n\nWelcome back, ' + userName + '!\nYour access level: ' + userInfo.subscriptionType + '\n\nProcessing your request...';
  } else if (isProcessingRequest) {
    blockReason = 'pending_approval';
    const req = ProcessingRequests.find(r => r.userId === userId);
    const reqTime = req && req.requestTime ? new Date(req.requestTime).toLocaleString() : 'Unknown';
    userMessage = '‚è≥ <b>Access Pending</b>\n\nHello ' + userName + ',\n\nYour access request is currently being reviewed by an administrator. You will be notified once a decision has been made.\n\nRequest status: <code>PENDING</code>\nSubmitted: ' + reqTime;
  } else {
    blockReason = 'not_authorized';
    userMessage = 'üö´ <b>Access Denied</b>\n\nHello ' + userName + ',\n\nYou are not authorized to use this bot. To request access, please use the /request command or contact an administrator.\n\nYour User ID: <code>' + userId + '</code>';

    if (!isRequestCommand) {
      adminMessage = 'üîî <b>Unauthorized Access Attempt</b>\n\n<b>User Details:</b>\n‚Ä¢ Name: ' + userName + '\n‚Ä¢ User ID: <code>' + userId + '</code>\n‚Ä¢ Language: ' + languageCode + '\n‚Ä¢ Time: ' + currentTime + '\n‚Ä¢ Message: ' + messageText + '\n\n<b>Action Required:</b>\nReview and approve/deny access';
    }
  }

  const item = $input.item;
  item.json.botGuard = {
    allowEntry: allowEntry,
    blockReason: blockReason,
    userMessage: userMessage,
    adminMessage: adminMessage,
    authorizedMessage: authorizedMessage,
    isProcessingRequest: isProcessingRequest,
    isRequestCommand: isRequestCommand,
    userId: userId,
    userName: userName,
    chatId: chatId,
    messageText: messageText,
    languageCode: languageCode,
    accessAttemptTime: currentTime,
    AllowedUsers: AllowedUsers,
    Administrators: Administrators,
    originalMessage: item.json.message,
    userDetails: {
      subscriptionType: userInfo.subscriptionType,
      firstName: (message.from && message.from.first_name) || '',
      lastName: (message.from && message.from.last_name) || '',
      isBot: (message.from && message.from.is_bot) || false,
      isPremium: (message.from && message.from.is_premium) || false
    }
  };

  item.json.message = message;
  return item;
} catch (error) {
  const item = $input.item;
  item.json.botGuard = {
    allowEntry: false,
    blockReason: 'error',
    userMessage: '‚ùå An error occurred during authorization. Please try again.',
    adminMessage: '',
    authorizedMessage: '',
    error: error.message
  };
  return item;
}

// Get data from Check Authorization node (before Telegram node)
const botGuardData = $('Check Authorization').item.json.botGuard;
const admins = botGuardData.Administrators;
const adminMessage = botGuardData.adminMessage;

// Create output item for each admin
const items = admins.map(admin => ({
  json: {
    adminChatId: admin.chatId,
    adminUserName: admin.userName,
    adminMessage: adminMessage,
    originalData: botGuardData
  }
}));

return items;
