// Bezpieczny getter do $()
// Zwraca { json: {} } gdy node nie istnieje lub nie ma danych
function pickNodeFirst(name) {
  try {
    const arr = $(name).all();
    if (Array.isArray(arr) && arr.length > 0) return arr[0];
  } catch (e) {
    // node nie istnieje ‚Äì zwr√≥ƒá pusty obiekt, ≈ºeby nie wywaliƒá wykonania
  }
  return { json: {} };
}

function safeGet(obj, path, def = null) {
  try {
    const parts = Array.isArray(path) ? path : String(path).split('.');
    return parts.reduce((acc, key) => (acc && acc[key] !== undefined ? acc[key] : def), obj);
  } catch {
    return def;
  }
}

