// Get the date from the Schedule Trigger
const today = new Date($input.item.json.timestamp);

// Calculate the previous month
const previousMonth = new Date(today.getFullYear(), today.getMonth() - 1, 1);

// Start date: last day of the month before the previous month
const startDate = new Date(previousMonth.getFullYear(), previousMonth.getMonth(), 0);

// End date: first day of the month after the previous month
const endDate = new Date(previousMonth.getFullYear(), previousMonth.getMonth() + 1, 1);

// Format as YYYY-MM-DD
const startDateStr = startDate.toISOString().split('T')[0];
const endDateStr = endDate.toISOString().split('T')[0];

// Get the report month and year
const monthNames = [
  'January', 'February', 'March', 'April', 'May', 'June',
  'July', 'August', 'September', 'October', 'November', 'December'
];
const reportMonth = monthNames[previousMonth.getMonth()];
const reportYear = previousMonth.getFullYear();

return {
  json: {
    month: `${reportMonth} ${reportYear}`,
    startDate: startDateStr,
    endDate: endDateStr
  }
};

