// Business hours configuration
const WORK_START = $('Variables').first().json.WORK_START;   // 8 AM
const LUNCH_START = $('Variables').first().json.LUNCH_START; // 12 PM
const LUNCH_END = $('Variables').first().json.WORK_END;   // 1 PM
const WORK_END = $('Variables').first().json.WORK_END;    // 6 PM

// SLA configuration
const SLA_LIMIT_HOURS = $('Variables').first().json.SLA_LIMIT_HOURS; // cambiar aqu√≠ seg√∫n SLA

function toDate(str) {
  return str ? new Date(str.replace(" ", "T")) : null;
}

function isWeekend(date) {
  const day = date.getDay();
  return day === 0 || day === 6;
}

// Calculate effective working hours within business schedule
function businessHoursDiff(start, end) {
  if (!start || !end) return 0;
  if (end < start) return 0;

  let totalHours = 0;
  let current = new Date(start);

  while (current < end) {
    if (isWeekend(current)) {
      current.setDate(current.getDate() + 1);
      current.setHours(WORK_START, 0, 0, 0);
      continue;
    }

    const workMorningStart = new Date(current.setHours(WORK_START, 0, 0, 0));
    const workMorningEnd = new Date(current.setHours(LUNCH_START, 0, 0, 0));
    const workAfternoonStart = new Date(current.setHours(LUNCH_END, 0, 0, 0));
    const workAfternoonEnd = new Date(current.setHours(WORK_END, 0, 0, 0));

    const dayStart = start > workMorningStart ? start : workMorningStart;
    const dayEnd = end < workAfternoonEnd ? end : workAfternoonEnd;

    if (dayStart < workMorningEnd) {
      totalHours += Math.max(0, (Math.min(dayEnd, workMorningEnd) - dayStart) / 3600000);
    }
    if (dayEnd > workAfternoonStart) {
      totalHours += Math.max(0, (dayEnd - Math.max(dayStart, workAfternoonStart)) / 3600000);
    }

    current.setDate(current.getDate() + 1);
    current.setHours(WORK_START, 0, 0, 0);
  }

  return totalHours;
}

// Convert decimal ‚Üí "Xh Ym"
function decimalToHM(decimal) {
  const hours = Math.floor(decimal);
  const minutes = Math.round((decimal - hours) * 60);
  return `${hours}h ${minutes}m`;
}

// Initialize metrics
let general = { open: 0, in_progress: 0, solved: 0, closed: 0, hours: 0, sla_ok: 0, total: 0 };
let byTechnician = {};

// Process tickets
for (const item of items) {
  const t = item.json;

  const opening = toDate(t["Opening date"]);
  const solution = toDate(t["Solution date"]);
  const closing = toDate(t["Closing date"]);
  const technician = t.technical_id || "Unassigned";
  const status = parseInt(t.Status, 10);

  const end = solution || closing;
  const hours = end ? businessHoursDiff(opening, end) : 0;
  const sla_ok = solution ? (hours <= SLA_LIMIT_HOURS ? 1 : 0) : 0;

  // General report
  if (status === 1) general.open++;
  if (status === 2) general.in_progress++;
  if (status === 5) general.solved++;
  if (status === 6) general.closed++;
  general.hours += hours;
  general.sla_ok += sla_ok;
  general.total++;

  // Report by technician
  if (!byTechnician[technician]) {
    byTechnician[technician] = { open: 0, in_progress: 0, solved: 0, closed: 0, hours: 0, sla_ok: 0, total: 0 };
  }
  if (status === 1) byTechnician[technician].open++;
  if (status === 2) byTechnician[technician].in_progress++;
  if (status === 5) byTechnician[technician].solved++;
  if (status === 6) byTechnician[technician].closed++;
  byTechnician[technician].hours += hours;
  byTechnician[technician].sla_ok += sla_ok;
  byTechnician[technician].total++;
}

// Function to build summary
function summary(data) {
  return {
    "Total cases": data.total,
    "Open cases": data.open,
    "Cases in progress": data.in_progress,
    "Solved cases": data.solved,
    "Closed cases": data.closed,
    "Total hours (decimal)": data.hours.toFixed(2),
    "Total hours (formatted)": decimalToHM(data.hours),
    "Average hours (decimal)": data.total > 0 ? (data.hours / data.total).toFixed(2) : 0,
    "Average hours (formatted)": data.total > 0 ? decimalToHM(data.hours / data.total) : "0h 0m",
    "SLA compliance": data.total > 0 ? ((data.sla_ok / data.total) * 100).toFixed(2) + "%" : "0%"
  };
}

// Final output
return [
  {
    json: {
      "General Report": summary(general),
      "Report by Technician": Object.fromEntries(
        Object.entries(byTechnician).map(([tech, data]) => [tech, summary(data)])
      )
    }
  }
];
