// n8n Function Node
// Keep only required fields + extract clean domain from companyWebsite
// Wrap output in "company" wrapper

return items.map(item => {
  const data = item.json;

  let domain = null;
  if (data.companyWebsite) {
    let website = data.companyWebsite.trim();

    // If no protocol, prepend http://
    if (!/^https?:\/\//i.test(website)) {
      website = "http://" + website;
    }

    try {
      const parsedUrl = new URL(website);
      domain = parsedUrl.hostname.replace(/^www\./, ""); // remove "www."
    } catch (err) {
      // Fallback: strip query/hash manually
      domain = website
        .replace(/^(https?:\/\/)?(www\.)?/, "")
        .split(/[/?#]/)[0]; // take only domain part
    }
  }

  return {
    json: {
      company: { // üëà wrapper added
        companyName: data.companyName || null,
        companyLinkedInUrl: data.companyLinkedInUrl || data.companyLinkedinUrl || null,
        companyWebsite: data.companyWebsite || null,
        industries: data.industries || null,
        location: data.location || null,
        employeeCount: data.companyEmployeesCount || null,
        jobTitle: data.jobTitle || data.title || null,
        companyDescription: data.companyDescription || null,
        domain: domain
      }
    }
  };
});

