// N8N JavaScript Code Node - Clean Data Merging (No Debug)
// Mapping Logic:
// - person.personId <-> email.id (one-to-one, unique)
// - person.companyName <-> company.companyName (many-to-one, multiple persons per company)

// Get all input items
const allItems = $input.all();

// Step 1: Build email lookup by personId (one-to-one mapping)
const emailMap = {};
allItems.forEach(item => {
  const jsonData = item.json || {};
  if (jsonData.email && jsonData.email.id) {
    emailMap[jsonData.email.id] = jsonData.email.email;
  }
});

// Step 2: Build company lookup by companyName (many-to-one mapping)
const companyMap = {};
allItems.forEach(item => {
  const jsonData = item.json || {};
  if (jsonData.company && jsonData.company.companyName) {
    companyMap[jsonData.company.companyName] = jsonData.company;
  }
});

// Step 3: Merge person with email + company info
const outputItems = [];

allItems.forEach(item => {
  const jsonData = item.json || {};
  if (!jsonData.person) return;

  const person = jsonData.person;
  const personId = person.personId;
  const companyName = person.companyName;

  // Get email from emailMap (unique per person)
  const email = emailMap[personId] || null;

  // Get company info from companyMap (same for all persons at this company)
  const companyInfo = companyMap[companyName] || {};

  // Create merged record
  const mergedItem = {
    json: {
      ...person, // Spread all person fields
      email: email,
      companyDescription: companyInfo.companyDescription || null,
      jobTitle: companyInfo.jobTitle || null,
      employeeCount: companyInfo.employeeCount || null
    }
  };

  outputItems.push(mergedItem);
});

// Return the merged results
return outputItems;

// Flatten + dedupe by (person.id + companyName)
let results = [];
let seen = new Set();

items.forEach(item => {
  const people = item.json.people || [];

  people.forEach(person => {
    if (!person.id) return;

    const company = person.organization || {};
    const uniqueKey = person.id + "|" + (company.name || ""); 

    if (seen.has(uniqueKey)) return;
    seen.add(uniqueKey);

    let websiteUrl = company.website_url || null;
    let domain = company.primary_domain || null;

    if (!domain && websiteUrl) {
      try {
        const parsedUrl = new URL(websiteUrl);
        domain = parsedUrl.hostname.replace(/^www\./, "");
      } catch (err) {
        domain = websiteUrl.replace(/^(https?:\/\/)?(www\.)?/, "").split(/[/?#]/)[0];
      }
    }

    results.push({
      json: {
        person: {   // üëà wrapped object
          uniqueKey: uniqueKey,
          personId: person.id || null,
          firstName: person.first_name || null,
          lastName: person.last_name || null,
          personLinkedInUrl: person.linkedin_url || null,
          title: person.title || null,
          city: person.city || null,
          country: person.country || null,
          companyName: company.name || null,
          companyWebsite: websiteUrl,
          primaryDomain: domain,
          companyLinkedInUrl: company.linkedin_url || null
        }
      }
    });
  });
});

return results;
