let aiAnalysis;
try {
  const raw = $input.item.json.message?.content || '{}';
  aiAnalysis = JSON.parse(raw.replace(/```json\n?|```/g, ''));
} catch (e) {
  aiAnalysis = {
    sentiment: 'neutral',
    urgency_score: 5,
    intent: 'continue_intake',
    requires_escalation: false,
    detected_keywords: [],
    summary: $('Extract SMS Data1').item.json.message_body
  };
}

const sessionData = $('Find User Session1').item.json;
let intakeData = JSON.parse(sessionData.intake_data || '{}');
if (!intakeData.transcript) intakeData.transcript = [];
if (!intakeData.ai_metadata) intakeData.ai_metadata = {};

intakeData.transcript.push({
  message: $('Extract SMS Data1').item.json.message_body,
  timestamp: $('Extract SMS Data1').item.json.received_at,
  direction: 'incoming',
  ai_analysis: aiAnalysis
});

intakeData.ai_metadata.last_sentiment = aiAnalysis.sentiment;
intakeData.ai_metadata.last_intent = aiAnalysis.intent;
intakeData.ai_metadata.max_urgency = Math.max(intakeData.ai_metadata.max_urgency || 0, aiAnalysis.urgency_score);

return {
  session_id: sessionData.session_id,
  phone_number: sessionData.phone_number,
  status: sessionData.status,
  intake_data: JSON.stringify(intakeData),
  ai_analysis: aiAnalysis,
  original_message: $('Extract SMS Data1').item.json.message_body
};

