let enrichment;
try {
  const raw = $input.item.json.message?.content || '{}';
  enrichment = JSON.parse(raw.replace(/```json\n?|```/g, ''));
} catch (e) {
  enrichment = {extracted_info: {}, completeness_score: 0, suggested_next_question: 'Tell me more?', category: 'general'};
}

const sessionData = $('Process AI Analysis').item.json;
let intakeData = JSON.parse(sessionData.intake_data);

if (!intakeData.structured_data) intakeData.structured_data = {};
Object.assign(intakeData.structured_data, enrichment.extracted_info);

intakeData.ai_metadata.completeness_score = enrichment.completeness_score;
intakeData.ai_metadata.category = enrichment.category;
intakeData.ai_metadata.suggested_next_question = enrichment.suggested_next_question;

return {
  session_id: sessionData.session_id,
  phone_number: sessionData.phone_number,
  intake_data: JSON.stringify(intakeData),
  updated_at: new Date().toISOString(),
  suggested_response: enrichment.suggested_next_question,
  completeness_score: enrichment.completeness_score
};

const sessionData = $('Process AI Analysis').item.json;
let intakeData = JSON.parse(sessionData.intake_data);

intakeData.completed_at = new Date().toISOString();
intakeData.status = 'completed';

const filename = `transcripts/${sessionData.session_id}_${Date.now()}.json`;

return {
  session_id: sessionData.session_id,
  phone_number: sessionData.phone_number,
  intake_data: JSON.stringify(intakeData),
  status: 'completed',
  completed_at: intakeData.completed_at,
  final_transcript: JSON.stringify(intakeData, null, 2),
  s3_filename: filename
};

const code = Math.floor(100000 + Math.random() * 900000).toString();
const phoneNumber = $input.item.json.phone_number;
const expiryTime = new Date(Date.now() + 10 * 60 * 1000).toISOString();

return {
  phone_number: phoneNumber,
  verification_code: code,
  expires_at: expiryTime,
  created_at: new Date().toISOString(),
  verified: false
};

return {
  phone_number: $input.item.json.phone_number,
  submitted_code: $input.item.json.verification_code,
  current_time: new Date().toISOString()
};

return {
  from_number: $input.item.json.From,
  message_body: $input.item.json.Body || '',
  message_sid: $input.item.json.MessageSid,
  received_at: new Date().toISOString()
};

return {
  session_id: $('Process AI Analysis').item.json.session_id,
  status: 'escalated',
  escalated_at: new Date().toISOString()
};
