// Filter deals with status = 'won' and not yet surveyed
const deals = $input.all();
const completedDeals = [];

for (const deal of deals) {
  const data = deal.json;

  // Check if deal status is 'won' (completed in GHL)
  if (data.status && data.status.toLowerCase() === 'won') {

    // Check if NPS survey was already sent
    let npsSent = false;
    if (data.customFields && Array.isArray(data.customFields)) {
      const npsField = data.customFields.find(field => field.key === 'nps_survey_sent');
      npsSent = npsField ? npsField.value === true || npsField.value === 'true' : false;
    }

    // Only process deals where survey hasn't been sent
    if (!npsSent) {
      // Extract contact details
      const contact = data.contact || {};
      const email = contact.email || '';
      const phone = contact.phone || '';

      // Skip if no email (can't send survey)
      if (!email) {
        console.log(`Skipping deal ${data.id} - no email found`);
        continue;
      }

      completedDeals.push({
        json: {
          dealId: data.id,
          clientName: contact.name || 'Client',
          clientEmail: email,
          clientPhone: phone,
          dealValue: data.monetaryValue || 0,
          completedDate: data.lastStatusChangeAt || new Date().toISOString(),
          contactId: contact.id || '',
          companyName: contact.companyName || '',
          pipelineId: data.pipelineId || '',
          hasDeals: true
        }
      });
    }
  }
}

// Return appropriate response
if (completedDeals.length === 0) {
  return [{ json: { hasDeals: false, message: 'No new completed deals found', count: 0 } }];
}

console.log(`Found ${completedDeals.length} completed deals ready for NPS survey`);
return completedDeals;

// Extract NPS score and details from email snippet
const emailData = $input.first().json;
const snippet = emailData.snippet || '';

// Extract score from snippet (e.g., "Score: 2")
const scoreMatch = snippet.match(/Score:\s*(\d+)/);
const score = scoreMatch ? parseInt(scoreMatch[1]) : 0;

// Extract deal ID from snippet
const dealIdMatch = snippet.match(/Deal ID:\s*([^\s]+)/);

// Extract client name from snippet
const clientMatch = snippet.match(/Client:\s*([^\n\r]+?)(?=\s*Email:|$)/);

// Extract email from snippet
const emailMatch = snippet.match(/Email:\s*([^\s]+)/);

// Extract feedback (everything after "Please share your feedback:")
const feedbackMatch = snippet.match(/Please share your feedback:\s*([\s\S]*)/);

return [{
  json: {
    dealId: dealIdMatch ? dealIdMatch[1].trim() : '',
    clientName: clientMatch ? clientMatch[1].trim() : '',
    clientEmail: emailMatch ? emailMatch[1].trim() : '',
    npsScore: score,
    category: score >= 9 ? 'Promoter' : (score >= 7 ? 'Passive' : 'Detractor'),
    feedback: feedbackMatch ? feedbackMatch[1].trim() : '',
    timestamp: new Date().toISOString(),
    historyId: emailData.historyId || '',
    emailSubject: snippet.substring(0, 50)
  }
}];
