// ---- STEP 1: Separate inputs ----
const allRows = $input.all();

// Identify rows with candidate info (Hire Tracking) vs traits (Retention Summary)
const candidateRaw = allRows.filter(r => r.json["Candidate "] || r.json.Candidate);
const traitsRaw = allRows.filter(r => r.json.Trait);

// ---- STEP 2: Normalize Candidate Data ----
const candidates = candidateRaw.map(c => {
  return {
    row_number: c.json.row_number,
    Candidate: (c.json["Candidate "] || c.json.Candidate || "").trim(),
    Role: (c.json["Role              "] || c.json.Role || "").trim(),
    Traits: (c.json["Traits                   "] || c.json.Traits || "").trim(),
    Start_Date: c.json["Start Date"] || null,
    Status: (c.json["Status   "] || c.json.Status || "").trim(),
    Retention_30: c.json["Retention_30 "] ?? c.json.Retention_30 ?? null,
    Retention_90: c.json["Retention_90"] ?? null,
    Candidate_Score: c.json["Candidate_Score"] || 0
  };
});

// ---- STEP 3: Normalize Traits Summary ----
const traits = traitsRaw.map(t => {
  return {
    Trait: t.json.Trait,
    Total_Hires: parseInt(t.json.Total_Hires, 10) || 0,
    Stayed_90: parseInt(t.json.Stayed_90, 10) || 0,
    Left_90: parseInt(t.json.Left_90, 10) || 0,
    "Retention_%": parseFloat(t.json["Retention_%"]) || 0,
    Weight_Adjust: parseInt(t.json.Weight_Adjust, 10) || 0,
    Candidate_Score: t.json.Candidate_Score || 0
  };
});

// ---- STEP 4: Build Trait Weight Lookup ----
const weightMap = {};
for (const t of traits) {
  weightMap[t.Trait] = t.Weight_Adjust;
}

// ---- STEP 5: Calculate Candidate Scores ----
for (const c of candidates) {
  let score = 0;
  const candidateTraits = (c.Traits || "").split(",").map(t => t.trim());
  candidateTraits.forEach(trait => {
    score += weightMap[trait] ?? 0;
  });
  c.Candidate_Score = score;
}

// ---- STEP 6: Final Combined Output ----
return [
  {
    json: {
      candidates,
      traits
    }
  }
];
