const htmlContent = $node["Company Homepage Scraper"].json["data"]; // Replace this with the field containing your HTML content

// Function to strip HTML tags and clean the text
const extractPlainText = (html) => {
    if (!html) return "";

    // Remove script and style tags along with their content
    const withoutScriptsAndStyles = html.replace(/<script[\s\S]*?>[\s\S]*?<\/script>/gi, "")
                                        .replace(/<style[\s\S]*?>[\s\S]*?<\/style>/gi, "");

    // Remove all remaining HTML tags
    const strippedText = withoutScriptsAndStyles.replace(/<\/?[^>]+(>|$)/g, "");

    // Replace HTML entities with their plain-text equivalents
    const decodedText = strippedText.replace(/&nbsp;/gi, " ")
                                    .replace(/&amp;/gi, "&")
                                    .replace(/&lt;/gi, "<")
                                    .replace(/&gt;/gi, ">")
                                    .replace(/&quot;/gi, "\"")
                                    .replace(/&#39;/gi, "'");

    // Replace multiple spaces, tabs, and newlines with a single space
    const cleanedText = decodedText.replace(/\s+/g, " ").trim();

    return cleanedText;
};

// Call the function to process the HTML content
const plainText = extractPlainText(htmlContent);

// Return the plain text as the output of this node
return {
    plainText: plainText,
};

const items = $input.all();

const cleanText = (html) => {
    return html
        .replace(/<script[\s\S]*?>[\s\S]*?<\/script>/gi, '')  // Remove scripts
        .replace(/<style[\s\S]*?>[\s\S]*?<\/style>/gi, '')    // Remove styles
        .replace(/<!--[\s\S]*?-->/g, '')                     // Remove comments
        .replace(/<\/?[^>]+(>|$)/g, '')                      // Remove HTML tags
        .replace(/&nbsp;/gi, " ")                           // Decode HTML entities
        .replace(/&amp;/gi, "&")
        .replace(/&lt;/gi, "<")
        .replace(/&gt;/gi, ">")
        .replace(/&quot;/gi, "\"")
        .replace(/&#39;/gi, "'")
        .replace(/\s+/g, ' ')                                // Normalize whitespace
        .trim();
};

const updatedItems = items.map((item) => {
    const data = item.json.data.map(cleanText);
    return { ...item.json, data };
});

return updatedItems;

