const inputData = $node["Company Homepage Scraper"].json.data; // Reference the correct node

function extractHeaderLinks(htmlString) {
    const headerLinks = htmlString.match(/<a[^>]+href=["'](.*?)["'][^>]*>(.*?)<\/a>/g);

    if (!headerLinks) return [];

    return headerLinks.map(link => {
        const urlMatch = link.match(/href=["'](.*?)["']/);
        const textMatch = link.match(/>(.*?)<\/a>/);
        return {
            text: textMatch ? textMatch[1].trim() : '',
            url: urlMatch ? urlMatch[1] : ''
        };
    }).filter(item => item.url); // Remove any empty URLs
}

return extractHeaderLinks(inputData);

// Assume input is an array of post objects (from previous node)
// n8n passes each item as { json: { ...post } }, so get all items
const items = $input.all();

const mergedText = items.map(item => {
  const p = item.json;
  return [
    `Author: ${p.author?.first_name || ''} ${p.author?.last_name || ''}`.trim(),
    `Date: ${p.posted_at?.date || ''}`,
    `Type: ${p.post_type || ''}`,
    `Text: ${p.text || ''}`,
    `URL: ${p.url || ''}`
  ].filter(Boolean).join('\n');
}).join('\n---\n');

return [
  {
    json: {
      merged_posts: mergedText
    }
  }
];
