// n8n JavaScript Code to Extract Search Results Data

// Get the input data (assuming it's in $input.all()[0].json)
const inputData = $input.all()[0].json;

// Initialize the output object
const output = {
  query: inputData.searchParameters?.q || "N/A",
  topTwoResults: [],
  peopleAlsoAskExists: "No",
  topThreeRelatedSearches: []
};

// Extract top 2 search results
if (inputData.organic && Array.isArray(inputData.organic)) {
  const topTwo = inputData.organic.slice(0, 2);

  output.topTwoResults = topTwo.map(result => ({
    position: result.position,
    title: result.title,
    url: result.link,
    snippet: result.snippet,
    date: result.date || "N/A",
    sitelinks: result.sitelinks || [],
    scrapeOutput: result.scrapeOutput ? {
      title: result.scrapeOutput.title,
      description: result.scrapeOutput.metadata?.description || "N/A",
      ogTitle: result.scrapeOutput.metadata?.ogTitle || "N/A",
      ogDescription: result.scrapeOutput.metadata?.ogDescription || "N/A",
      url: result.scrapeOutput.url,
      language: result.scrapeOutput.metadata?.language || "N/A"
    } : null
  }));
}

// Check if "People Also Ask" exists and extract questions
if (inputData.peopleAlsoAsk && Array.isArray(inputData.peopleAlsoAsk) && inputData.peopleAlsoAsk.length > 0) {
  output.peopleAlsoAskExists = "Yes";
  output.peopleAlsoAskQuestions = inputData.peopleAlsoAsk.map(item => ({
    question: item.question,
    snippet: item.snippet,
    title: item.title,
    link: item.link
  }));
} else {
  output.peopleAlsoAskQuestions = [];
}

// Extract top 3 related searches
if (inputData.relatedSearches && Array.isArray(inputData.relatedSearches)) {
  output.topThreeRelatedSearches = inputData.relatedSearches
    .slice(0, 3)
    .map(search => search.query);
}

// Return the output
return output;

