// Removes new lines from message.content on every item
// Paste into an n8n Code node set to JavaScript

return items.map((item) => {
  const obj = item.json ?? item;

  // Get the content wherever it lives
  let content =
    obj?.message?.content ??
    obj?.content ??
    "";

  // Replace new lines with spaces, collapse extra spaces, and trim
  const cleaned = String(content)
    .replace(/(\r\n|\n|\r)/g, " ")
    .replace(/\s{2,}/g, " ")
    .trim();

  // Put it back where it came from
  if (obj?.message && typeof obj.message === "object") {
    obj.message.content = cleaned;
  } else {
    obj.content = cleaned;
  }

  item.json = obj;
  return item;
});
