// Get all processed items from the loop
const items = $input.all();

// Log for debugging
console.log(`Processing ${items.length} items for statistics`);

// Initialize counters
const stats = {
  total: items.length,
  valid: 0,
  invalid: 0,
  risky: 0,
  unknown: 0,
  processed_at: new Date().toLocaleString('en-IN', {
    dateStyle: 'full',
    timeStyle: 'short'
  }),
  sheet_url: $('Read Email List').params?.documentId?.__rl?.cachedResultUrl || 'YOUR_GOOGLE_SHEET_URL'
};

// Count each category
items.forEach(item => {
  const status = item.json.status;

  if (status === 'valid') {
    stats.valid++;
  } else if (status === 'invalid') {
    stats.invalid++;
  } else if (status === 'risky') {
    stats.risky++;
  } else {
    stats.unknown++;
  }
});

// Calculate percentages (handle division by zero)
if (stats.total > 0) {
  stats.valid_percentage = ((stats.valid / stats.total) * 100).toFixed(1);
  stats.invalid_percentage = ((stats.invalid / stats.total) * 100).toFixed(1);
  stats.risky_percentage = ((stats.risky / stats.total) * 100).toFixed(1);
  stats.unknown_percentage = ((stats.unknown / stats.total) * 100).toFixed(1);
} else {
  stats.valid_percentage = '0.0';
  stats.invalid_percentage = '0.0';
  stats.risky_percentage = '0.0';
  stats.unknown_percentage = '0.0';
}

// Calculate list health score (0-100)
// Formula: 
// - Valid emails: +100 points per email (percentage of total)
// - Invalid emails: -20 points per email (penalty)
// - Risky emails: -10 points per email (smaller penalty)
stats.health_score = Math.max(0, Math.min(100, Math.round(
  (stats.valid / stats.total) * 100 - 
  (stats.invalid / stats.total) * 20 - 
  (stats.risky / stats.total) * 10
)));

// Determine health status
if (stats.health_score >= 80) {
  stats.health_status = 'Excellent ‚úÖ';
  stats.health_color = '#4CAF50';
} else if (stats.health_score >= 60) {
  stats.health_status = 'Good ‚ö†Ô∏è';
  stats.health_color = '#ff9800';
} else {
  stats.health_status = 'Needs Attention ‚ùå';
  stats.health_color = '#f44336';
}

// Create summary text for notifications
stats.summary = `üìä Email List Validation Complete

‚úÖ Valid: ${stats.valid} (${stats.valid_percentage}%)
‚ùå Invalid: ${stats.invalid} (${stats.invalid_percentage}%)
‚ö†Ô∏è Risky: ${stats.risky} (${stats.risky_percentage}%)
‚ùì Unknown: ${stats.unknown} (${stats.unknown_percentage}%)

üìà List Health Score: ${stats.health_score}/100 - ${stats.health_status}
üìß Total Processed: ${stats.total} emails
üïê Completed: ${stats.processed_at}`;

// Create detailed lists for the report
stats.email_details = {
  valid_emails: items
    .filter(item => item.json.status === 'valid')
    .map(item => item.json.email),

  invalid_emails: items
    .filter(item => item.json.status === 'invalid')
    .map(item => ({
      email: item.json.email,
      reason: item.json.notes
    })),

  risky_emails: items
    .filter(item => item.json.status === 'risky')
    .map(item => ({
      email: item.json.email,
      reason: item.json.notes
    }))
};

// Log summary for debugging
console.log(stats.summary);

// Return the complete statistics object
return stats;

// Get the validation result from current input
const validation = $input.first().json;

// Get the ORIGINAL data from Loop Over Items node
const loopData = $('Process Each Email').item.json;

// Process valid email
let status = 'valid';
let notes = 'Email verified successfully';

// Check for any warnings even if valid
if (validation.details?.disposable === true) {
  status = 'risky';
  notes = 'Valid but disposable email address';
}

const checkedAt = new Date().toLocaleString('en-IN', { 
  year: 'numeric',
  month: '2-digit',
  day: '2-digit',
  hour: '2-digit',
  minute: '2-digit',
  hour12: true
});

// Return merged data - combining Loop data + validation results
return {
  row_number: loopData.row_number,
  name: loopData.name,
  email: loopData.email,
  status: status,
  checked_at: checkedAt,
  notes: notes,
  is_valid: true
};

// Get the validation result
const validation = $input.first().json;

// Get original data from Loop Over Items
const loopData = $('Process Each Email').item.json;

// Process invalid email
let status = 'invalid';
let notes = 'Email validation failed';

// Get more specific reason from validation details
if (validation.details) {
  if (validation.details.validMxRecord === false) {
    notes = 'Invalid - No mail server found';
  } else if (validation.details.rfcCompliant === false) {
    notes = 'Invalid - Incorrect email format';
  } else if (validation.details.spoofFree === false) {
    notes = 'Invalid - Possible spoof/fake email';
  }
}

const checkedAt = new Date().toLocaleString('en-IN', { 
  year: 'numeric',
  month: '2-digit',
  day: '2-digit',
  hour: '2-digit',
  minute: '2-digit',
  hour12: true
});

// Return merged data
return {
  row_number: loopData.row_number,
  name: loopData.name,
  email: loopData.email,
  status: status,
  checked_at: checkedAt,
  notes: notes,
  is_valid: false
};

