const items = $input.all();
const guestData = items[0].json.body;
const email = guestData.email || null;
const firstName = guestData.first_name || '';
const lastName = guestData.last_name || '';
const phone = guestData.phone_number || null;
const propertyAddress = guestData.openHouseTitle || '';
const visitDate = guestData.submissionTimestamp || new Date().toISOString();
const hasAgent = guestData.are_you_currently_working_with_an_agent || 'Not specified';
const rating = guestData.what_did_you_rate_the_house || null;
const buyerAgreement = guestData.do_you_have_a_signed_buyer_agreement || null;
let leadScore = 50;
if (hasAgent === 'No' || hasAgent === 'no') leadScore += 30;
if (rating && parseInt(rating) >= 4) leadScore += 20;
if (rating && parseInt(rating) <= 2) leadScore -= 20;
if (buyerAgreement === 'No' || buyerAgreement === 'no') leadScore += 10;
leadScore = Math.max(0, Math.min(100, leadScore));
let leadStatus = 'OPEN';
if (leadScore >= 70) leadStatus = 'HOT';
else if (leadScore >= 50) leadStatus = 'WARM';
else if (leadScore < 40) leadStatus = 'COLD';
const qualifiesForFollowUp = (hasAgent.toLowerCase() === 'no' || (hasAgent.toLowerCase() === 'yes' && buyerAgreement && buyerAgreement.toLowerCase() === 'no'));
const notes = `Property: ${propertyAddress}\nVisit: ${visitDate}\nHas Agent: ${hasAgent}\nLead Score: ${leadScore}\nStatus: ${leadStatus}`;
return [{json: {email, firstname: firstName, lastname: lastName, phone, propertyAddress: propertyAddress, visitDate, hasAgent, rating, buyerAgreement, leadScore, leadStatus, qualifiesForFollowUp, notes, lifecyclestage: 'lead', _raw: guestData}}];
