// Certificate HTML Generator
const items = $input.all();

return items.map(item => {
  const data = item.json;

  // Generate unique certificate ID if not provided
  const certId = data.certificateId || `CERT-${Date.now()}-${Math.random().toString(36).substr(2, 9).toUpperCase()}`;

  // Format date nicely
  const certDate = new Date(data.date).toLocaleDateString('en-US', {
    year: 'numeric',
    month: 'long',
    day: 'numeric'
  });

  // HTML Template with inline CSS
  const htmlContent = `
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Certificate of Completion</title>
    <style>
        @import url('https://fonts.googleapis.com/css2?family=Playfair+Display:wght@400;700&family=Montserrat:wght@300;400;600&display=swap');

        * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
        }

        body {
            width: 1200px;
            height: 850px;
            font-family: 'Montserrat', sans-serif;
            background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
            display: flex;
            justify-content: center;
            align-items: center;
            padding: 40px;
        }

        .certificate {
            background: white;
            width: 100%;
            height: 100%;
            border: 20px solid #f8f9fa;
            box-shadow: 0 20px 60px rgba(0,0,0,0.3);
            position: relative;
            padding: 60px 80px;
            display: flex;
            flex-direction: column;
            justify-content: space-between;
        }

        .certificate::before {
            content: '';
            position: absolute;
            top: 35px;
            left: 35px;
            right: 35px;
            bottom: 35px;
            border: 3px solid #667eea;
            pointer-events: none;
        }

        .header {
            text-align: center;
            margin-bottom: 20px;
        }

        .logo {
            width: 80px;
            height: 80px;
            margin: 0 auto 15px;
            background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
            border-radius: 50%;
            display: flex;
            align-items: center;
            justify-content: center;
            color: white;
            font-size: 36px;
            font-weight: bold;
        }

        .title {
            font-family: 'Playfair Display', serif;
            font-size: 48px;
            font-weight: 700;
            color: #2d3748;
            letter-spacing: 2px;
            margin-bottom: 10px;
        }

        .subtitle {
            font-size: 18px;
            color: #718096;
            text-transform: uppercase;
            letter-spacing: 3px;
            font-weight: 300;
        }

        .content {
            text-align: center;
            flex-grow: 1;
            display: flex;
            flex-direction: column;
            justify-content: center;
        }

        .presented-to {
            font-size: 16px;
            color: #718096;
            text-transform: uppercase;
            letter-spacing: 2px;
            margin-bottom: 20px;
        }

        .recipient-name {
            font-family: 'Playfair Display', serif;
            font-size: 56px;
            font-weight: 700;
            color: #667eea;
            margin-bottom: 30px;
            padding-bottom: 15px;
            border-bottom: 3px solid #e2e8f0;
            display: inline-block;
            min-width: 500px;
        }

        .description {
            font-size: 18px;
            color: #4a5568;
            line-height: 1.8;
            margin-bottom: 25px;
            max-width: 700px;
            margin-left: auto;
            margin-right: auto;
        }

        .course-name {
            font-weight: 600;
            color: #2d3748;
            font-size: 24px;
            margin: 20px 0;
        }

        .footer {
            display: flex;
            justify-content: space-between;
            align-items: flex-end;
            margin-top: 40px;
        }

        .signature-block {
            text-align: center;
            flex: 1;
        }

        .signature-line {
            border-top: 2px solid #2d3748;
            width: 250px;
            margin: 0 auto 10px;
            padding-top: 5px;
        }

        .signature-name {
            font-weight: 600;
            color: #2d3748;
            font-size: 16px;
            margin-bottom: 5px;
        }

        .signature-title {
            font-size: 14px;
            color: #718096;
        }

        .date-block {
            text-align: center;
        }

        .date-label {
            font-size: 12px;
            color: #718096;
            text-transform: uppercase;
            letter-spacing: 1px;
            margin-bottom: 5px;
        }

        .date-value {
            font-weight: 600;
            color: #2d3748;
            font-size: 16px;
        }

        .cert-id {
            position: absolute;
            bottom: 15px;
            right: 25px;
            font-size: 10px;
            color: #a0aec0;
            letter-spacing: 1px;
        }

        .badge {
            position: absolute;
            top: 50px;
            right: 60px;
            width: 100px;
            height: 100px;
            background: linear-gradient(135deg, #ffd700 0%, #ffed4e 100%);
            border-radius: 50%;
            display: flex;
            align-items: center;
            justify-content: center;
            box-shadow: 0 4px 15px rgba(255, 215, 0, 0.4);
        }

        .badge::before {
            content: '‚òÖ';
            font-size: 50px;
            color: white;
        }
    </style>
</head>
<body>
    <div class="certificate">
        <div class="badge"></div>

        <div class="header">
            <div class="logo">C</div>
            <h1 class="title">Certificate of Completion</h1>
            <p class="subtitle">This certifies that</p>
        </div>

        <div class="content">
            <div class="recipient-name">${data.name}</div>

            <p class="description">
                has successfully completed the course
            </p>

            <div class="course-name">${data.course}</div>

            ${data.duration ? `<p class="description" style="margin-top: 10px; font-size: 16px;">Duration: ${data.duration}</p>` : ''}
        </div>

        <div class="footer">
            <div class="signature-block">
                <div class="signature-line"></div>
                <div class="signature-name">${data.instructor || 'Program Director'}</div>
                <div class="signature-title">Instructor</div>
            </div>

            <div class="date-block">
                <div class="date-label">Date of Completion</div>
                <div class="date-value">${certDate}</div>
            </div>
        </div>

        <div class="cert-id">Certificate ID: ${certId}</div>
    </div>
</body>
</html>
  `;

  return {
    json: {
      ...data,
      certificateId: certId,
      html: htmlContent,
      formattedDate: certDate
    }
  };
});

