// Combine Webhook Data with Email Validation
// Get the current item (from IF node - email validation data)
const emailData = $input.item.json;

// Get the original webhook data from the first node
const webhookData = $('Certificate Request Webhook').first().json.body;

// Combine everything
return {
  json: {
    // Certificate data from webhook
    name: webhookData.name,
    course: webhookData.course,
    date: webhookData.date,
    email: webhookData.email,
    instructor: webhookData.instructor || 'Program Director',
    duration: webhookData.duration || '',
    certificateId: webhookData.certificateId || '',

    // Email validation results (optional - for logging/debugging)
    emailValidation: {
      valid: emailData.valid,
      provider: emailData.details.mx.provider,
      rfcCompliant: emailData.details.rfcCompliant,
      validMxRecord: emailData.details.validMxRecord,
      disposable: emailData.details.disposable
    }
  }
};
