const input = $input.first().json.output;
const meetingData = $('extractMeetingData').first().json;

// Parse the AI output to extract structured data
const sections = input.split('**');
let summary = '';
let actionItems = [];
let keyDates = [];
let sentiment = 'Neutral';

for (let i = 0; i < sections.length; i++) {
  if (sections[i].includes('Meeting Summary:')) {
    summary = sections[i + 1].trim();
  }
  if (sections[i].includes('Action Items:')) {
    const items = sections[i + 1].trim().split('\n').filter(line => line.startsWith('-'));
    actionItems = items.map(item => {
      const match = item.match(/- (.+?) \(Assigned to: (.+?), Due: (.+?)\)/);
      if (match) {
        return {
          task: match[1],
          assignee: match[2],
          dueDate: match[3]
        };
      }
      return { task: item.replace('- ', ''), assignee: 'Unassigned', dueDate: 'No date' };
    });
  }
  if (sections[i].includes('Key Dates:')) {
    const dates = sections[i + 1].trim().split('\n').filter(line => line.startsWith('-'));
    keyDates = dates.map(d => d.replace('- ', ''));
  }
  if (sections[i].includes('Sentiment:')) {
    sentiment = sections[i + 1].trim();
  }
}

return {
  json: {
    meetingTitle: meetingData.meetingTitle,
    meetingDate: meetingData.meetingStart,
    participants: meetingData.participants,
    summary: summary,
    actionItems: actionItems,
    keyDates: keyDates,
    sentiment: sentiment,
    rawOutput: input
  }
};
