// Get the raw text
const raw = $input.first().json.content;

// Remove ```json and ``` wrappers + escaped \n
const cleaned = raw
  .replace(/```json/g, "")
  .replace(/```/g, "")
  .replace(/\\n/g, "")
  .trim();

// Parse to JSON
const data = JSON.parse(cleaned);

return [{ json: data }];
