// === Upstream node names (update if you renamed) ===
const META_NODE = 'Build Week Window2';
const HTTP_NODE = 'Get Impression data';

// ---------- Helpers ----------
const pad = n => String(n).padStart(2, '0');

function coerceBody(x) {
  const b = x?.body ?? x;
  if (b == null) return {};
  if (typeof b === 'string') { try { return JSON.parse(b); } catch { return {}; } }
  return b;
}

function toNumber(v) {
  if (v == null) return 0;
  if (typeof v === 'number') return Number.isFinite(v) ? v : 0;
  if (typeof v === 'string') {
    const n = Number(v.replace?.(/,/g, '') ?? v);
    return Number.isFinite(n) ? n : 0;
  }
  if (typeof v === 'object') {
    if ('value' in v) return toNumber(v.value);
    if ('intValue' in v) return toNumber(v.intValue);
    if ('doubleValue' in v) return toNumber(v.doubleValue);
    if ('count' in v) return toNumber(v.count);
  }
  return 0;
}

function getPoints(ts) {
  if (!ts) return [];
  if (Array.isArray(ts)) return ts;
  return ts.datedValues || ts.dailyValues || ts.values || [];
}

function sumPoints(arr) {
  if (!Array.isArray(arr)) return 0;
  let sum = 0;
  for (const d of arr) sum += toNumber(d?.value ?? d?.count ?? d);
  return sum;
}

// ---------- Main ----------
const metas = $items(META_NODE, 0, 0).map(i => i.json);
const httpItems = $items(HTTP_NODE, 0, 0).map(i => i.json || i);
const out = [];

for (let i = 0; i < metas.length; i++) {
  const meta = metas[i] || {};
  const http = httpItems[i] || {};
  const body = coerceBody(http);

  // âœ… FIX: Use Map to ensure each metric is only counted once
  const metricMap = new Map();

  const allEntries = body?.multiDailyMetricTimeSeries ?? [];

  for (const entry of allEntries) {
    const series = entry?.dailyMetricTimeSeries ?? [];
    for (const s of series) {
      const metricKey = s?.dailyMetric || s?.metric;
      if (!metricKey) continue;

      // Only process if we haven't seen this metric yet
      if (!metricMap.has(metricKey)) {
        const points = getPoints(s?.timeSeries);
        metricMap.set(metricKey, sumPoints(points));
      }
    }
  }

  // Convert Map to object for easy access
  const totals = Object.fromEntries(metricMap);

  // Extract known metric values
  const DESKTOP_SEARCH = totals.BUSINESS_IMPRESSIONS_DESKTOP_SEARCH || 0;
  const MOBILE_SEARCH  = totals.BUSINESS_IMPRESSIONS_MOBILE_SEARCH  || 0;
  const DESKTOP_MAPS   = totals.BUSINESS_IMPRESSIONS_DESKTOP_MAPS   || 0;
  const MOBILE_MAPS    = totals.BUSINESS_IMPRESSIONS_MOBILE_MAPS    || 0;

  const impressions_search = DESKTOP_SEARCH + MOBILE_SEARCH;
  const impressions_maps   = DESKTOP_MAPS  + MOBILE_MAPS;

  // âœ… Only this key is used for directions
  const direction_requests = totals.BUSINESS_DIRECTION_REQUESTS || 0;

  out.push({
    json: {
      company: meta.company || '(unknown)',
      locationId: meta.locationId ?? null,

      week_start: `${meta.startY}-${pad(meta.startM)}-${pad(meta.startD)}`,
      week_end:   `${meta.endY}-${pad(meta.endM)}-${pad(meta.endD)}`,

      website_clicks: totals.WEBSITE_CLICKS || 0,
      call_clicks: totals.CALL_CLICKS || 0,
      direction_requests,

      impressions_search,
      impressions_maps,

      impressions_search_desktop: DESKTOP_SEARCH,
      impressions_search_mobile:  MOBILE_SEARCH,
      impressions_maps_desktop:   DESKTOP_MAPS,
      impressions_maps_mobile:    MOBILE_MAPS,
    }
  });
}

return out;

// Build items from the "Read Companies" node, not from the incoming payload
const companies = $items('Read Companies').map(i => i.json);

return companies.map(c => ({
  json: {
    companyName: c.company,
    // keep both the full path and the raw IDs for convenience
    accountId: String(c.accountId),
    locationId: String(c.locationId),
    accountPath: `accounts/${c.accountId}`,
    locationPath: `locations/${c.locationId}`,
  }
}));

// Builds "this week" (last 7 full days ending yesterday, UTC)
// AND "last week" (the 7 days before that), as a single shared window item.

function atUtcMidnight(d = new Date()) {
  return new Date(Date.UTC(d.getUTCFullYear(), d.getUTCMonth(), d.getUTCDate()));
}

const todayMid = atUtcMidnight();
const endThis = new Date(todayMid); endThis.setUTCDate(endThis.getUTCDate() - 1);
const startThis = new Date(endThis); startThis.setUTCDate(startThis.getUTCDate() - 6);

const endLast = new Date(startThis); endLast.setUTCDate(endLast.getUTCDate() - 1);
const startLast = new Date(endLast); startLast.setUTCDate(startLast.getUTCDate() - 6);

const parts = d => ({
  Y: d.getUTCFullYear(),
  M: d.getUTCMonth() + 1,
  D: d.getUTCDate(),
});
const isoDate = d => d.toISOString().slice(0,10);

const sT = parts(startThis), eT = parts(endThis);
const sL = parts(startLast), eL = parts(endLast);

return [{
  json: {
    // THIS WEEK
    startY: sT.Y, startM: sT.M, startD: sT.D,
    endY:   eT.Y, endM: eT.M, endD: eT.D,
    startISO: startThis.toISOString(),
    endISO:   endThis.toISOString(),
    week_start: isoDate(startThis),
    week_end:   isoDate(endThis),

    // LAST WEEK
    last_startY: sL.Y, last_startM: sL.M, last_startD: sL.D,
    last_endY:   eL.Y, last_endM: eL.M, last_endD: eL.D,
    last_startISO: startLast.toISOString(),
    last_endISO:   endLast.toISOString(),
    last_week_start: isoDate(startLast),
    last_week_end:   isoDate(endLast),

    // Numeric timestamps for easy filtering/comparison in later code nodes
    ts: {
      startThis: startThis.getTime(),
      endThis:   endThis.getTime() + 24*60*60*1000 - 1,
      startLast: startLast.getTime(),
      endLast:   endLast.getTime() + 24*60*60*1000 - 1,
    },
  }
}];

// Weekly Reviews Summary â€” Input: items from the weekly mapper above
function avg(a){ return a.length ? +(a.reduce((x,y)=>x+y,0)/a.length).toFixed(2) : 0; }
function median(a){
  if (!a.length) return 0;
  const s=[...a].sort((x,y)=>x-y), m=Math.floor(s.length/2);
  return s.length%2 ? s[m] : +(((s[m-1]+s[m])/2).toFixed(2));
}

const rows = $input.all().map(i => i.json);

// group by company + week_start (use location too if you have multi-location per company)
const groups = new Map();
for (const r of rows) {
  const key = `${r.companyName||'(unknown)'}__${r.week_start||''}`;
  if (!groups.has(key)) groups.set(key, []);
  groups.get(key).push(r);
}

const out = [];
for (const [, list] of groups.entries()) {
  const companyName = list[0].companyName || '(unknown)';
  const locationId  = list[0].locationId || null;
  const week_start  = list[0].week_start || null;
  const week_end    = list[0].week_end || null;

  const total = list.length;
  const ratings = list.map(r => Number(r.ratingNum||0)).filter(n => Number.isFinite(n));
  const avgRating = avg(ratings);

  const pos = list.filter(r => r.sentiment_label === 'Positive').length;
  const neu = list.filter(r => r.sentiment_label === 'Neutral').length;
  const neg = list.filter(r => r.sentiment_label === 'Negative').length;
  const positiveShare = total ? +((pos/total)*100).toFixed(1) : 0;

  const replied = list.filter(r => r.hasReply === true).length;
  const replyRatePct = total ? +((replied/total)*100).toFixed(1) : 0;

  const latencies = list.map(r => (typeof r.replyLatencyHours === 'number') ? r.replyLatencyHours : null).filter(v => v !== null);
  const medianReplyLatencyHours = median(latencies);

  out.push({
    json: {
      companyName, locationId, week_start, week_end,
      total_reviews: total,
      positive: pos, neutral: neu, negative: neg,
      positive_share_pct: positiveShare,
      average_rating: avgRating,
      reply_rate_pct: replyRatePct,
      median_reply_latency_hours: medianReplyLatencyHours
    }
  });
}

return out;

// Build items from the "Read Companies" node, not from the incoming payload
const companies = $items('Read Companies').map(i => i.json);

return companies.map(c => ({
  json: {
    companyName: c.company,
    // keep both the full path and the raw IDs for convenience
    accountId: String(c.accountId),
    locationId: String(c.locationId),
    accountPath: `accounts/${c.accountId}`,
    locationPath: `locations/${c.locationId}`,
  }
}));

// Builds a date window covering the last 12 full ISO weeks ending yesterday
const now = new Date();
const end = new Date(Date.UTC(now.getUTCFullYear(), now.getUTCMonth(), now.getUTCDate()));
end.setUTCDate(end.getUTCDate() - 1); // yesterday

// go back ~84 days (12 weeks)
const start = new Date(end);
start.setUTCDate(start.getUTCDate() - (7 * 12 - 1)); // inclusive start

return $input.all().map(({ json }) => {
  return {
    json: {
      ...json,
      startY: start.getUTCFullYear(),
      startM: start.getUTCMonth() + 1,
      startD: start.getUTCDate(),
      endY:   end.getUTCFullYear(),
      endM:   end.getUTCMonth() + 1,
      endD:   end.getUTCDate()
    }
  };
});

