// === Upstream node names (change if you renamed) ===
const META_NODE = 'Build 12-Week Window';
const HTTP_NODE = '12 week impressions';

// Pull aligned arrays from the two upstream nodes
const metas = $items(META_NODE, 0, 0).map(i => i.json);
const httpItems = $items(HTTP_NODE, 0, 0).map(i => i.json || i);

// helpers
const pad = n => String(n).padStart(2, '0');

function coerceBody(x) {
  const b = x?.body ?? x;
  if (b == null) return {};
  if (typeof b === 'string') {
    try { return JSON.parse(b); } catch { return {}; }
  }
  return b;
}

function toUTCDate(y, m, d) {
  if (y == null || m == null || d == null) return null;
  return new Date(Date.UTC(Number(y), Number(m) - 1, Number(d)));
}

function getWindow(meta) {
  const start = toUTCDate(meta.startY, meta.startM, meta.startD);
  const end   = toUTCDate(meta.endY,   meta.endM,   meta.endD);
  return { start, end };
}

function parsePointDate(p) {
  // Google often returns { date: { year, month, day } }
  const dd = p?.date || p?.startDate || p?.endDate || p?.time?.startDate || p?.time?.endDate;
  if (!dd) return null;

  if (typeof dd === 'string') {
    const d = new Date(dd); // ISO string case
    return isNaN(d) ? null : d;
  }

  // object form { year, month, day }
  const y = dd.year ?? dd?.year;
  const m = dd.month ?? dd?.month;
  const d = dd.day ?? dd?.day;
  const dt = toUTCDate(y, m, d);
  return dt ?? null;
}

function inRange(date, start, end) {
  if (!date || !start || !end) return true; // if anything missing, don't filter out
  return date >= start && date <= end;
}

function sumPointsInRange(arr, start, end) {
  if (!Array.isArray(arr)) return 0;
  let sum = 0;
  for (const p of arr) {
    const v = Number(p?.value ?? p?.count ?? 0);
    if (!Number.isFinite(v)) continue;
    const pd = parsePointDate(p);
    if (inRange(pd, start, end)) sum += v;
  }
  return sum;
}

const out = [];

for (let i = 0; i < metas.length; i++) {
  const meta = metas[i] || {};
  const http = httpItems[i] || {};
  const body = coerceBody(http);
  const { start, end } = getWindow(meta);

  // âœ… FIX: Use Map to deduplicate metrics
  const metricMap = new Map();

  const allEntries = body?.multiDailyMetricTimeSeries ?? [];

  for (const entry of allEntries) {
    const series = entry?.dailyMetricTimeSeries ?? [];
    for (const s of series) {
      const metric = s?.dailyMetric;
      if (!metric) continue;

      // Only process if we haven't seen this metric yet
      if (!metricMap.has(metric)) {
        const points = s?.timeSeries?.datedValues || s?.timeSeries?.dailyValues || [];
        metricMap.set(metric, sumPointsInRange(points, start, end));
      }
    }
  }

  // Convert Map to object
  const totals = Object.fromEntries(metricMap);

  // raw splits
  const DESKTOP_SEARCH = totals.BUSINESS_IMPRESSIONS_DESKTOP_SEARCH || 0;
  const MOBILE_SEARCH  = totals.BUSINESS_IMPRESSIONS_MOBILE_SEARCH  || 0;
  const DESKTOP_MAPS   = totals.BUSINESS_IMPRESSIONS_DESKTOP_MAPS   || 0;
  const MOBILE_MAPS    = totals.BUSINESS_IMPRESSIONS_MOBILE_MAPS    || 0;

  // combined
  const impressions_search = DESKTOP_SEARCH + MOBILE_SEARCH;
  const impressions_maps   = DESKTOP_MAPS  + MOBILE_MAPS;

  out.push({
    json: {
      company: meta.company || '(unknown)',
      locationId: meta.locationId || null,

      week_start: `${meta.startY}-${pad(meta.startM)}-${pad(meta.startD)}`,
      week_end:   `${meta.endY}-${pad(meta.endM)}-${pad(meta.endD)}`,

      // clicks & requests
      website_clicks: totals.WEBSITE_CLICKS || 0,
      call_clicks: totals.CALL_CLICKS || 0,
      direction_requests: totals.BUSINESS_DIRECTION_REQUESTS || 0,

      // combined
      impressions_search,
      impressions_maps,

      // granular splits
      impressions_search_desktop: DESKTOP_SEARCH,
      impressions_search_mobile:  MOBILE_SEARCH,
      impressions_maps_desktop:   DESKTOP_MAPS,
      impressions_maps_mobile:    MOBILE_MAPS,
    }
  });
}

return out;

