// === Upstream node names (change if yours differ) ===
const META_NODE = 'Build Week Window2';
const HTTP_NODE = 'Get Impressions (last week)'; // <- set to your last-week HTTP node name

// Pull aligned arrays
const metas = $items(META_NODE, 0, 0).map(i => i.json);
const httpItems = $items(HTTP_NODE, 0, 0).map(i => i.json || i);

const pad = n => String(n).padStart(2, '0');

function sumPoints(arr) {
  if (!Array.isArray(arr)) return 0;
  return arr.reduce((acc, d) => acc + Number(d?.value ?? d?.count ?? 0), 0);
}

const out = [];

for (let i = 0; i < metas.length; i++) {
  const meta = metas[i] || {};
  const http = httpItems[i] || {};
  const body = http.body ?? http;

  const series = body?.multiDailyMetricTimeSeries?.[0]?.dailyMetricTimeSeries || [];

  const totals = {};
  for (const s of series) {
    const points = s?.timeSeries?.datedValues || s?.timeSeries?.dailyValues || [];
    totals[s.dailyMetric] = sumPoints(points);
  }

  // splits
  const DESKTOP_SEARCH = totals.BUSINESS_IMPRESSIONS_DESKTOP_SEARCH || 0;
  const MOBILE_SEARCH  = totals.BUSINESS_IMPRESSIONS_MOBILE_SEARCH  || 0;
  const DESKTOP_MAPS   = totals.BUSINESS_IMPRESSIONS_DESKTOP_MAPS   || 0;
  const MOBILE_MAPS    = totals.BUSINESS_IMPRESSIONS_MOBILE_MAPS    || 0;

  // combined
  const impressions_search_last = DESKTOP_SEARCH + MOBILE_SEARCH;
  const impressions_maps_last   = DESKTOP_MAPS  + MOBILE_MAPS;

  out.push({
    json: {
      company: meta.company || '(unknown)',
      locationId: meta.locationId || null,

      // last-week window (from meta.last_*)
      last_week_start: `${meta.last_startY}-${pad(meta.last_startM)}-${pad(meta.last_startD)}`,
      last_week_end:   `${meta.last_endY}-${pad(meta.last_endM)}-${pad(meta.last_endD)}`,

      // actions
      website_clicks_last: totals.WEBSITE_CLICKS || 0,
      call_clicks_last: totals.CALL_CLICKS || 0,
      direction_requests_last: totals.BUSINESS_DIRECTION_REQUESTS || 0,

      // impressions (combined)
      impressions_search_last,
      impressions_maps_last,

      // impressions (splits)
      impressions_search_desktop_last: DESKTOP_SEARCH,
      impressions_search_mobile_last:  MOBILE_SEARCH,
      impressions_maps_desktop_last:   DESKTOP_MAPS,
      impressions_maps_mobile_last:    MOBILE_MAPS,
    }
  });
}

return out;

/***** Join All â€” robust comparisons (impressions + reviews + all-time)
 * Accepts mixed records per company/location:
 *  - This-week impressions (impressions_* + week_start/week_end)
 *  - Last-week impressions  (â€¦_last + last_week_start/last_week_end)
 *  - 12-week impressions rollup
 *  - Weekly reviews (this week)  -> { total_reviews, positive, neutral, negative, â€¦, week_start/week_end }
 *  - Weekly reviews (last week)  -> { â€¦ , isLastWeek:true } OR object with week_start == last_week_start
 *  - 12-week reviews summary     -> { total_reviews_12w, positive_share_pct_12w, â€¦, weeks }
 *  - All-time reviews            -> { total_reviews_all_time, positive_share_pct_all_time, â€¦ }
 *
 * Emits one row per company/location with normalized blocks:
 *  impressions, impressions_lastWeek, impressions_12w (+avg fields), vs12wAvg_impressions, vsLast_impressions
 *  reviews, reviews_lastWeek, reviews_12w (+avg fields), vsLast_reviews, vs12wAvg_reviews
 *  allTimeReviews (mapped if provided upstream)
 *****/

function n(v){ const x = Number(v); return Number.isFinite(x) ? x : 0; }
function nn(v){ return v == null ? null : Number(v); }
const round1 = v => (v == null ? null : +(+v).toFixed(1));
const pct = (num, den) => (den ? +(((+num)/(+den))*100).toFixed(1) : 0);
const pp = (a, b) => (a == null || b == null) ? null : +(+a - +b).toFixed(1);
const diffPct = (cur, prev) => (prev ? +(((+cur - +prev) / +prev) * 100).toFixed(1) : 0);

function daysBetween(aISO, bISO){
  const a = Date.parse(aISO), b = Date.parse(bISO);
  return (Number.isFinite(a) && Number.isFinite(b)) ? Math.abs((b-a)/86400000) : null;
}
function weekSpanCount(startISO, endISO){
  const d = daysBetween(startISO, endISO);
  if (d == null) return null;
  return Math.max(1, Math.round((d + 1) / 7));
}

function packImpressions(src, isLast=false){
  if (!src) return null;

  const s  = isLast ? n(src.impressions_search_last) : n(src.impressions_search);
  const m  = isLast ? n(src.impressions_maps_last)   : n(src.impressions_maps);
  const sd = isLast ? n(src.impressions_search_desktop_last) : n(src.impressions_search_desktop);
  const sm = isLast ? n(src.impressions_search_mobile_last)  : n(src.impressions_search_mobile);
  const md = isLast ? n(src.impressions_maps_desktop_last)   : n(src.impressions_maps_desktop);
  const mm = isLast ? n(src.impressions_maps_mobile_last)    : n(src.impressions_maps_mobile);
  const w  = isLast ? n(src.website_clicks_last) : n(src.website_clicks);
  const c  = isLast ? n(src.call_clicks_last)    : n(src.call_clicks);
  const d  = isLast ? n(src.direction_requests_last) : n(src.direction_requests);

  const total = s + m;
  const actions_total = w + c + d;
  const ctr_actions_pct = total ? +((actions_total / total) * 100).toFixed(1) : 0;

  return {
    week_start: isLast ? (src.last_week_start || src.week_start || null) : (src.week_start || null),
    week_end:   isLast ? (src.last_week_end   || src.week_end   || null) : (src.week_end   || null),

    search: s, maps: m, total,
    search_desktop: sd, search_mobile: sm,
    maps_desktop: md, maps_mobile: mm,

    website_clicks: w, call_clicks: c, direction_requests: d,
    actions_total, ctr_actions_pct
  };
}

// -------- collect + group ----------
const rows = $input.all().map(i => i.json);
const groups = new Map();
for (const r of rows){
  const company = r.companyName || r.company || '(unknown)';
  const loc = r.locationId ?? null;
  const key = `${company}__${loc ?? 'null'}`;
  if (!groups.has(key)) groups.set(key, []);
  groups.get(key).push(r);
}

const out = [];

for (const [key, list] of groups.entries()){
  const companyName = list[0].companyName || list[0].company || '(unknown)';
  const locationId  = list[0].locationId ?? null;

  let thisW_imp = null, lastW_imp = null, block12_imp = null;
  let thisW_rev = null, lastW_rev = null, block12_rev = null;
  let allTime = null;

  let anchor_last_start = null, anchor_last_end = null;

  for (const r of list){
    // impressions shapes
    const hasImp = (r.impressions_search !== undefined || r.impressions_maps !== undefined);
    const hasImpLast = (r.impressions_search_last !== undefined || r.website_clicks_last !== undefined);

    if (hasImp && r.week_start && r.week_end) {
      const spanDays = daysBetween(r.week_start, r.week_end);
      if (spanDays != null && spanDays >= 60) block12_imp = r;   // ~12w rollup
      else thisW_imp = r;                                        // 1 week
    }
    if (hasImpLast && (r.last_week_start || r.last_week_end)) lastW_imp = r;

    // weekly reviews (this week) â€” take most recent
    if (r.total_reviews !== undefined && r.week_start) {
      thisW_rev = (thisW_rev == null || r.week_start >= thisW_rev.week_start) ? r : thisW_rev;
    }
    // explicit last-week reviews
    if (r.total_reviews !== undefined && r.isLastWeek === true) lastW_rev = r;

    // 12-week reviews summary (any of these keys)
    if (r.total_reviews_12w !== undefined || r.positive_share_pct_12w !== undefined || r.weeks !== undefined) {
      block12_rev = r;
    }

    // all-time reviews (map common variants)
    if (!allTime && (r.total_reviews_all_time !== undefined || r.average_rating_all_time !== undefined ||
                     r.all_time_total !== undefined || r.avg_rating_all_time !== undefined)) {
      allTime = {
        total: n(r.total_reviews_all_time ?? r.all_time_total),
        positiveShare: nn(r.positive_share_pct_all_time ?? r.all_time_positive_share_pct) ?? 0,
        averageRating: nn(r.average_rating_all_time ?? r.avg_rating_all_time),
        replyRatePct:  nn(r.reply_rate_pct_all_time ?? r.all_time_reply_rate_pct) ?? 0,
        medianReplyLatencyHours: nn(r.median_reply_latency_hours_all_time ?? r.all_time_median_reply_latency_hours),
        lastReviewAt: r.last_review_at ?? r.all_time_last_review_at ?? null
      };
    }

    // anchors
    if (!anchor_last_start && (r.last_week_start || r.last_startISO)) {
      anchor_last_start = r.last_week_start || (r.last_startISO && r.last_startISO.slice(0,10)) || null;
    }
    if (!anchor_last_end && (r.last_week_end || r.last_endISO)) {
      anchor_last_end = r.last_week_end || (r.last_endISO && r.last_endISO.slice(0,10)) || null;
    }
  }

  // try find last-week reviews by date if not explicitly flagged
  if (!lastW_rev && anchor_last_start) {
    lastW_rev = list.find(x => x.total_reviews !== undefined && x.week_start === anchor_last_start) || null;
  }

  // ---- impressions packing ----
  const impressions          = packImpressions(thisW_imp, false);
  const impressions_lastWeek = packImpressions(lastW_imp,  true);

  // 12w impressions totals & avgs (and CTR avg)
  let impressions_12w = null, vs12wAvg_impressions = null;
  if (block12_imp){
    const search = n(block12_imp.impressions_search);
    const maps   = n(block12_imp.impressions_maps);
    const total  = search + maps;

    const clicks = n(block12_imp.website_clicks);
    const calls  = n(block12_imp.call_clicks);
    const dirs   = n(block12_imp.direction_requests);
    const actions_total_12w = clicks + calls + dirs;

    const weeks = weekSpanCount(block12_imp.week_start, block12_imp.week_end) || 12;
    const avg_week_total = weeks ? +(total / weeks).toFixed(1) : 0;
    const avg_week_actions_total = weeks ? +((actions_total_12w / weeks).toFixed(1)) : 0;
    const avg_week_ctr_pct = avg_week_total ? +( (avg_week_actions_total / avg_week_total) * 100 ).toFixed(1) : 0;

    impressions_12w = {
      start: block12_imp.week_start || null,
      end:   block12_imp.week_end   || null,
      search, maps, total,
      website_clicks: clicks,
      call_clicks: calls,
      direction_requests: dirs,
      search_desktop: n(block12_imp.impressions_search_desktop),
      search_mobile:  n(block12_imp.impressions_search_mobile),
      maps_desktop:   n(block12_imp.impressions_maps_desktop),
      maps_mobile:    n(block12_imp.impressions_maps_mobile),
      avg_week_total,
      avg_week_actions_total,
      avg_week_ctr_pct
    };

    if (impressions){
      const diff = +(impressions.total - avg_week_total).toFixed(1);
      const pctVs = avg_week_total ? +((impressions.total/avg_week_total)*100).toFixed(1) : 0;
      vs12wAvg_impressions = {
        weeks,
        avg_week_total,
        pct_vs_avg: pctVs,
        diff_from_avg: diff,
        arrow: diff > 0 ? 'â†‘' : diff < 0 ? 'â†“' : 'â†”'
      };
    }
  }

  // vs last week (impressions) â€” include CTR delta
  let vsLast_impressions = null, impressions_delta = null;
  if (impressions && impressions_lastWeek){
    const tThis = impressions.total, tLast = impressions_lastWeek.total;
    const diffAbs = tThis - tLast;
    impressions_delta = { arrow: diffAbs>0?'â†‘':diffAbs<0?'â†“':'â†”', diffAbs };
    vsLast_impressions = {
      last_total: tLast,
      diff_total: diffAbs,
      pct_change_total: diffPct(tThis, tLast),
      ctr_this: impressions.ctr_actions_pct,
      ctr_last: impressions_lastWeek.ctr_actions_pct,
      ctr_diff: pp(impressions.ctr_actions_pct, impressions_lastWeek.ctr_actions_pct)
    };
  }

  // ---- reviews (this & last) ----
  const reviews = thisW_rev ? {
    total: n(thisW_rev.total_reviews),
    pos: n(thisW_rev.positive),
    neu: n(thisW_rev.neutral),
    neg: n(thisW_rev.negative),
    positiveShare: nn(thisW_rev.positive_share_pct) ?? 0,
    averageRating: nn(thisW_rev.average_rating),
    replyRatePct:  nn(thisW_rev.reply_rate_pct) ?? 0,
    medianReplyLatencyHours: nn(thisW_rev.median_reply_latency_hours)
  } : { total:0, pos:0, neu:0, neg:0, positiveShare:0, averageRating:null, replyRatePct:0, medianReplyLatencyHours:null };

  const reviews_lastWeek = lastW_rev ? {
    total: n(lastW_rev.total_reviews),
    pos: n(lastW_rev.positive),
    neu: n(lastW_rev.neutral),
    neg: n(lastW_rev.negative),
    positiveShare: nn(lastW_rev.positive_share_pct) ?? 0,
    averageRating: nn(lastW_rev.average_rating),
    replyRatePct:  nn(lastW_rev.reply_rate_pct) ?? 0,
    medianReplyLatencyHours: nn(lastW_rev.median_reply_latency_hours),
    week_start: lastW_rev.week_start || null,
    week_end:   lastW_rev.week_end   || null
  } : null;

  // ---- 12-week reviews rollup ----
  let reviews_12w = null, vsLast_reviews = null, vs12wAvg_reviews = null;
  if (block12_rev){
    const weeks = n(block12_rev.weeks) || 12;
    const total12 = n(block12_rev.total_reviews_12w);
    const pos12   = n(block12_rev.positive_12w);
    const neu12   = n(block12_rev.neutral_12w);
    const neg12   = n(block12_rev.negative_12w);

    const avg_total = weeks ? +(total12 / weeks).toFixed(1) : 0;
    const avg_pos   = weeks ? +(pos12   / weeks).toFixed(1) : 0;
    const avg_neu   = weeks ? +(neu12   / weeks).toFixed(1) : 0;
    const avg_neg   = weeks ? +(neg12   / weeks).toFixed(1) : 0;

    const avg_positiveShare = nn(block12_rev.positive_share_pct_12w) ?? 0;
    const avg_rating        = nn(block12_rev.average_rating_12w);
    const avg_replyRatePct  = nn(block12_rev.reply_rate_pct_12w) ?? 0;
    const avg_medianReplyLatencyHours = nn(block12_rev.median_reply_latency_hours_12w);

    reviews_12w = {
      weeks,
      total: total12,
      pos: pos12, neu: neu12, neg: neg12,
      avg_total, avg_pos, avg_neu, avg_neg,
      avg_positiveShare, avg_rating, avg_replyRatePct, avg_medianReplyLatencyHours
    };

    if (reviews){
      const diff = +(reviews.total - avg_total).toFixed(1);
      const pctVs = avg_total ? +((reviews.total/avg_total)*100).toFixed(1) : 0;
      vs12wAvg_reviews = {
        pct_vs_avg: pctVs,
        diff_from_avg: diff,
        arrow: diff > 0 ? 'â†‘' : diff < 0 ? 'â†“' : 'â†”'
      };
    }
  }

  if (reviews && reviews_lastWeek){
    const diff = reviews.total - reviews_lastWeek.total;
    vsLast_reviews = {
      last_total: reviews_lastWeek.total,
      diff_total: diff,
      pct_change_total: diffPct(reviews.total, reviews_lastWeek.total)
    };
  }

  // ---- emit ----
  out.push({
    json: {
      companyName,
      locationId,
      week_start: impressions?.week_start || thisW_rev?.week_start || null,
      week_end:   impressions?.week_end   || thisW_rev?.week_end   || null,

      impressions: impressions || null,
      impressions_lastWeek: impressions_lastWeek || null,
      impressions_12w: impressions_12w || null,
      vs12wAvg_impressions,
      vsLast_impressions,
      impressions_delta: vsLast_impressions ? { arrow: vsLast_impressions.diff_total>0?'â†‘':vsLast_impressions.diff_total<0?'â†“':'â†”', diffAbs: vsLast_impressions.diff_total } : null,

      reviews,
      reviews_lastWeek,
      reviews_12w,
      vsLast_reviews,
      vs12wAvg_reviews,

      // new: all-time (if present upstream)
      allTimeReviews: allTime || {}
    }
  });
}

return out;

// All-time Reviews â†’ KPIs (no LLM)
// Inputs:
//  - Get Many Reviews  (all reviews for each location)
//  - Set Company (total reviews)  (company â†” locationId mapping)
// Output: 1 item per company+location with lifetime KPIs

// --- helpers ---
function normIso(s){ return s ? String(s).replace(/\.(\d{3})\d*Z$/, '.$1Z') : null; }
function msSafe(s){ const t = Date.parse(normIso(s)); return Number.isFinite(t) ? t : null; }
function toNum(star){ const m={ONE:1,TWO:2,THREE:3,FOUR:4,FIVE:5}; return typeof star==='number'?star:(m[String(star||'').toUpperCase()] ?? 0); }
function pickLocationId(j){
  if (j.locationId) return String(j.locationId);
  if (j.locationPath) return String(j.locationPath).split('/').pop();
  if (j.location) return String(j.location).split('/').pop();
  if (j.name) {
    const p = String(j.name).split('/'); const i=p.indexOf('locations');
    if (i>-1 && p[i+1]) return p[i+1];
  }
  return null;
}
function inferSentiment(j){
  // rating-only heuristic (fast, consistent)
  const n = Number(j.ratingNum || toNum(j.starRating));
  if (!Number.isFinite(n) || n <= 0) return null;
  if (n === 3) return 'Neutral';
  if (n >= 4)  return 'Positive';
  if (n <= 2)  return 'Negative';
  return null;
}
function avg(a){ return a.length ? +(a.reduce((x,y)=>x+y,0)/a.length).toFixed(2) : 0; }
function median(a){
  if (!a.length) return 0;
  const s=[...a].sort((x,y)=>x-y), m=Math.floor(s.length/2);
  return s.length%2 ? s[m] : +(((s[m-1]+s[m])/2).toFixed(2));
}

// build company index from Set Company (total reviews)
const seed = $items('Set Company (total reviews)') || [];
const companyIndex = new Map();
for (const { json:s } of seed) {
  const locId = (s.locationId && String(s.locationId))
             || (s.locationPath && String(s.locationPath).split('/').pop())
             || null;
  if (locId) companyIndex.set(locId, s.companyName || s.company || '(unknown)');
}

// normalize all incoming reviews
const rows = [];
for (const { json } of $input.all()){
  const locationId  = pickLocationId(json);
  const companyName = json.companyName || companyIndex.get(locationId) || '(unknown)';
  if (!locationId) continue; // skip ghosts

  const ratingNum  = Number(json.ratingNum || toNum(json.starRating) || 0);
  const createISO  = json.createTime || json.created || null;

  // reply latency
  let latency = (typeof json.replyLatencyHours === 'number') ? json.replyLatencyHours : null;
  if (latency === null && json.reviewReply?.updateTime && createISO) {
    const msC = msSafe(createISO), msR = msSafe(json.reviewReply.updateTime);
    if (msC != null && msR != null) latency = Math.round(((msR - msC)/36e5)*100)/100;
  }

  rows.push({
    companyName,
    locationId,
    ratingNum,
    sentiment: inferSentiment(json),
    hasReply: !!(json.hasReply || json.reviewReply?.comment),
    replyLatencyHours: (typeof latency === 'number') ? latency : null,
    createdMs: msSafe(createISO),
  });
}

// aggregate per company+location
const byKey = new Map();
for (const r of rows) {
  if (!r.sentiment) continue; // skip unrated/unknown
  const key = `${r.companyName}__${r.locationId}`;
  if (!byKey.has(key)) byKey.set(key, []);
  byKey.get(key).push(r);
}

const out = [];
for (const [, list] of byKey.entries()){
  const companyName = list[0].companyName;
  const locationId  = list[0].locationId;

  const total = list.length;
  const pos = list.filter(x => x.sentiment === 'Positive').length;
  const neu = list.filter(x => x.sentiment === 'Neutral').length;
  const neg = list.filter(x => x.sentiment === 'Negative').length;

  const ratings = list.map(x => Number(x.ratingNum||0)).filter(Number.isFinite);
  const average_rating = avg(ratings);
  const positive_share_pct = total ? +((pos/total)*100).toFixed(1) : 0;

  const replied = list.filter(x => x.hasReply === true).length;
  const reply_rate_pct = total ? +((replied/total)*100).toFixed(1) : 0;

  const latencies = list.map(x => (typeof x.replyLatencyHours === 'number') ? x.replyLatencyHours : null).filter(v=>v!==null);
  const median_reply_latency_hours = median(latencies);

  const lastReviewAt = list.map(x => x.createdMs).filter(Number.isFinite).sort((a,b)=>a-b).pop() || null;

  out.push({
    json: {
      companyName,
      locationId,
      total_reviews_all_time: total,
      positive_all_time: pos,
      neutral_all_time: neu,
      negative_all_time: neg,
      positive_share_pct_all_time: positive_share_pct,
      average_rating_all_time: average_rating,
      reply_rate_pct_all_time: reply_rate_pct,
      median_reply_latency_hours_all_time: median_reply_latency_hours,
      last_review_at: lastReviewAt ? new Date(lastReviewAt).toISOString() : null
    }
  });
}

return out;

// Map + categorize Weekly Google Reviews in ONE node
// Depends on:
//   - Set Company (reviews): creates company index (companyName, locationPath)
//   - Build Week Window (reviews): provides startISO/endISO (or it will fallback to Build Week Window2)

const companies = $items('Set Company (reviews)') || [];
if (!companies.length) throw new Error('Set Company (reviews) not found or empty');

// Get week window
const winItems = $items('Build Week Window (reviews)') || $items('Build Week Window2') || [];
const win = winItems[0]?.json || {};
const week_start = win.startISO?.slice(0,10) || win.week_start || null;
const week_end   = win.endISO?.slice(0,10)   || win.week_end   || null;

// Build locationId -> companyName index
const companyIndex = new Map();
for (const {json:c} of companies) {
  const locId = (c.locationPath || '').split('/').pop();
  if (locId) companyIndex.set(String(locId), c.companyName || c.company || '(unknown)');
}

// Helpers
function toNum(star){
  const m = { ONE:1, TWO:2, THREE:3, FOUR:4, FIVE:5 };
  return typeof star === 'number' ? star : (m[String(star||'').toUpperCase()] ?? 0);
}
function normIso(s){ return s ? String(s).replace(/\.(\d{3})\d*Z$/, '.$1Z') : null; }
function msSafe(s){ const t = s ? Date.parse(normIso(s)) : NaN; return Number.isFinite(t) ? t : null; }
function withinWeek(ms){
  if (!ms) return false;
  const startMs = msSafe(week_start+'T00:00:00Z');
  const endMs   = msSafe(week_end  +'T23:59:59Z'); // inclusive
  return Number.isFinite(startMs) && Number.isFinite(endMs) && ms >= startMs && ms <= endMs;
}
function classify(ratingNum){
  const n = Number(ratingNum || 0);
  if (!Number.isFinite(n) || n <= 0) return null;
  if (n === 3) return 'Neutral';
  if (n >= 4) return 'Positive';
  if (n <= 2) return 'Negative';
  return null;
}

const out = [];
for (const {json:r} of $input.all()){
  // IDs
  let locationId = r.locationId ? String(r.locationId) : null;
  let reviewId   = r.reviewId   ? String(r.reviewId)   : null;

  if ((!locationId || !reviewId) && r.name) {
    const p = String(r.name).split('/');
    const li = p.indexOf('locations'); if (li > -1 && p[li+1]) locationId ||= p[li+1];
    const ri = p.indexOf('reviews');   if (ri > -1 && p[ri+1]) reviewId   ||= p[ri+1];
  }

  const createdMs = msSafe(r.createTime || r.created);
  if (!withinWeek(createdMs)) continue; // keep only this week's reviews

  const replyMs = msSafe(r.reviewReply?.updateTime);
  const ratingNum = Number(r.ratingNum || toNum(r.starRating) || 0);
  const sentiment_label = classify(ratingNum);

  const companyName = r.companyName || companyIndex.get(locationId) || '(unknown)';

  out.push({
    json: {
      companyName,
      locationId,
      reviewId,
      createTime: createdMs ? new Date(createdMs).toISOString() : null,

      ratingNum,
      ratingText: r.ratingText || r.starRating || null,
      comment: (r.comment || '').toString(),

      hasReply: !!r.reviewReply?.comment,
      replyLatencyHours: (replyMs && createdMs) ? Math.round(((replyMs - createdMs)/36e5)*100)/100 : null,

      // weekly fields for grouping
      week_start,
      week_end,

      // sentiment
      sentiment_label,
      sentimentCategory: sentiment_label,

      // stable key if you need it
      key: [companyName, locationId || 'noLocation', week_start || 'noStart'].join('__'),
    }
  });
}

return out;

// n8n Code node â€” Build final HTML email from:
// - Base metrics from node "format" (single item)
// - Three one-liners from current input (Append Merge produced 3 items)
// Output: one item with { subject, html }

function num(n) { return Number(n) || 0; }
function fmtInt(n) { return num(n).toLocaleString('en-US'); }
function fmtPct(n, dp = 0) { return `${(Number(n) || 0).toFixed(dp)}%`; }
function fmtHours(h) { const v = Number(h) || 0; return `${Math.round(v*10)/10}h`; }
function nowStamp() { return new Date().toLocaleString('en-US', { hour: 'numeric', minute: '2-digit' }); }
function fmtDateISOtoMdy(iso) { if (!iso) return ''; const d = new Date(iso); return isNaN(d) ? iso : d.toLocaleDateString('en-US'); }

// ---------- 1) Load the base formatted item (match by locationId)
const me = $json; // current grouped company item
const myLoc = String(me.locationId || "").trim();

// find the matching "format" item for this locationId
const baseList = $items('format', 0, 0).map(i => i.json || {});
const base = baseList.find(x => String(x.locationId || "").trim() === myLoc) || {};
const companyName = (base.companyName || me.company || '').trim();
const locationId  = myLoc;
const periodStart = base.week_start;
const periodEnd   = base.week_end;

// Convenience: display numbers
const disp = base.display || {};
const dispImpr = disp.impressions || {};
const dispRev  = disp.reviews || {};

const thisImpr = dispImpr.this || {};
const lastImpr = dispImpr.last || {};
const avgImpr  = dispImpr.avg  || {};

const thisRev  = dispRev.this || {};
const lastRev  = dispRev.last || {};
const avgRev   = dispRev.avg  || {};

// KPI deltas (vs 12w) from base.kpi
const kpi = base.kpi || {};
const kpiImprDelta = Number(kpi.impressions_vs12w_pct || 0);
const kpiActDelta  = Number(kpi.actions_vs12w_pct || 0);
const kpiCtrPp     = Number(kpi.ctr_vs12w_pp || 0);
const kpiRevCnt    = Number(kpi.reviews_vs12w_count || 0);

// ---------- 2) Read three one-liners from current item (already grouped)
const liners = {
  overall:     String(($json.lines && $json.lines.overall)     || '').trim(),
  impressions: String(($json.lines && $json.lines.impressions) || '').trim(),
  reviews:     String(($json.lines && $json.lines.reviews)     || '').trim(),
};

// ---------- 3) KPI badge helpers (vs 12w)
function kpiBadgePct(val) {
  const v = Number(val) || 0;
  if (Math.abs(v) <= 1) return { text: '= vs 12w', cls:'pill pill-gray' };
