// Subject line
const subject = `Weekly Google Reviews & Impressions â€” ${companyName} (${fmtDateISOtoMdy(periodStart)}â€“${fmtDateISOtoMdy(periodEnd)})`;

// per-item mode must return ONE object
return { json: { ...$json, subject, html } };

// Build last-week-only window (Monâ€“Sun style: last 7 full days ending yesterday, UTC)

function atUtcMidnight(d = new Date()) {
  return new Date(Date.UTC(d.getUTCFullYear(), d.getUTCMonth(), d.getUTCDate()));
}

const todayMid = atUtcMidnight();                  // today 00:00Z
const endThis  = new Date(todayMid);               // yesterday 00:00Z will be end-of-this-week (exclusive)
endThis.setUTCDate(endThis.getUTCDate() - 1);

const startThis = new Date(endThis);               // not used downstream; here for clarity only
startThis.setUTCDate(startThis.getUTCDate() - 6);

const endLast = new Date(startThis);               // last week ends the day before this-week starts
endLast.setUTCDate(endLast.getUTCDate() - 1);

const startLast = new Date(endLast);
startLast.setUTCDate(startLast.getUTCDate() - 6);

const isoDate = d => d.toISOString().slice(0, 10);

return [{
  json: {
    // last-week window only
    startY: startLast.getUTCFullYear(),
    startM: startLast.getUTCMonth() + 1,
    startD: startLast.getUTCDate(),
    endY:   endLast.getUTCFullYear(),
    endM:   endLast.getUTCMonth() + 1,
    endD:   endLast.getUTCDate(),

    startISO: startLast.toISOString(),        // IMPORTANT: set to last week
    endISO:   endLast.toISOString(),          // IMPORTANT: set to last week
    week_start: isoDate(startLast),
    week_end:   isoDate(endLast),

    // convenience numeric timestamps (inclusive end)
    ts: {
      start: startLast.getTime(),
      end:   endLast.getTime() + 24*60*60*1000 - 1
    }
  }
}];

// Build items from the "Read Companies" node, not from the incoming payload
const companies = $items('Read Companies').map(i => i.json);

return companies.map(c => ({
  json: {
    companyName: c.company,
    // keep both the full path and the raw IDs for convenience
    accountId: String(c.accountId),
    locationId: String(c.locationId),
    accountPath: `accounts/${c.accountId}`,
    locationPath: `locations/${c.locationId}`,
  }
}));

// Build items from the "Read Companies" node, not from the incoming payload
const companies = $items('Read Companies').map(i => i.json);

return companies.map(c => ({
