// Map + categorize Google Reviews (12-WEEK window) â€” SAFE
// Accepts upstream reviews as $input (preferred) OR pulls from a reviews node if present.
// Tries multiple node names for company index + date window. No throws; proceeds best-effort.

// ---------- helpers ----------
function safeItems(name){
  try {
    const arr = $items(name, 0, 0);
    return Array.isArray(arr) ? arr.map(i => i.json) : [];
  } catch { return []; }
}
function normIso(s){ return s ? String(s).replace(/\.(\d{3})\d*Z$/, '.$1Z') : null; }
function msSafe(s){ const t = s ? Date.parse(normIso(s)) : NaN; return Number.isFinite(t) ? t : null; }
function toNum(star){
  const m = { ONE:1, TWO:2, THREE:3, FOUR:4, FIVE:5 };
  return typeof star === 'number' ? star : (m[String(star||'').toUpperCase()] ?? 0);
}
function classify(n){
  const x = Number(n || 0);
  if (!Number.isFinite(x) || x <= 0) return null;
  if (x === 3) return 'Neutral';
  if (x >= 4) return 'Positive';
  if (x <= 2) return 'Negative';
  return null;
}

// ---------- get company index (locationId -> companyName) ----------
const companySeed =
  safeItems('Set Company last 12 week reviews').length ? safeItems('Set Company last 12 week reviews') :
  safeItems('Set Company (reviews)').length            ? safeItems('Set Company (reviews)') :
  [];

const companyIndex = new Map();
for (const c of companySeed) {
  const locId = (c.locationPath || '').split('/').pop() || String(c.locationId || '');
  if (locId) companyIndex.set(String(locId), c.companyName || c.company || '(unknown)');
}

// ---------- get 12-week window (multiple fallbacks; default = last 12 weeks ending yesterday) ----------
function default12w(){
  const now = new Date();
  const end = new Date(Date.UTC(now.getUTCFullYear(), now.getUTCMonth(), now.getUTCDate()));
  end.setUTCDate(end.getUTCDate() - 1); // yesterday
  const start = new Date(end);
  start.setUTCDate(start.getUTCDate() - (7*12 - 1)); // inclusive start
  return { startISO: start.toISOString(), endISO: end.toISOString(), weeks: 12 };
}

const winNode =
  safeItems('Build 12-week window (reviews)')[0]?.json ||
  safeItems('Build 12-Week Window (reviews)')[0]?.json ||
  safeItems('Build 12-Week Window')[0]?.json ||
  default12w();

const startISO = winNode.startISO || winNode.week_start || default12w().startISO;
const endISO   = winNode.endISO   || winNode.week_end   || default12w().endISO;
const weeks    = Number(winNode.weeks || 12) || 12;
const startMs  = msSafe(startISO);
const endMsInc = (msSafe(endISO) ?? 0) + 24*60*60*1000 - 1;

// ---------- gather reviews input ----------
let reviews = $input.all().map(i => i.json);
if (!reviews.length) {
  // fallback if this node isnâ€™t wired after the reviews fetch
  const fromGbp = safeItems('Get all reviews');
  if (fromGbp.length) reviews = fromGbp;
}

// ---------- map ----------
const out = [];
for (const r of reviews) {
  // IDs
  let locationId = r.locationId ? String(r.locationId) : null;
  let reviewId   = r.reviewId   ? String(r.reviewId)   : null;

  if ((!locationId || !reviewId) && r.name){
    const p = String(r.name).split('/');
    const li = p.indexOf('locations'); if (li > -1 && p[li+1]) locationId ||= p[li+1];
    const ri = p.indexOf('reviews');   if (ri > -1 && p[ri+1]) reviewId   ||= p[ri+1];
  }

  const createdMs = msSafe(r.createTime || r.created);
  if (!(Number.isFinite(createdMs) && Number.isFinite(startMs) && Number.isFinite(endMsInc))) continue;
  if (createdMs < startMs || createdMs > endMsInc) continue; // keep only 12-week window

  const replyMs = msSafe(r.reviewReply?.updateTime);
  const ratingNum = Number(r.ratingNum || toNum(r.starRating) || 0);
  const sentiment_label = classify(ratingNum);

  const companyName = r.companyName || companyIndex.get(locationId) || '(unknown)';

  out.push({
    json: {
      companyName,
      locationId,
      reviewId,
      createTime: createdMs ? new Date(createdMs).toISOString() : null,

      ratingNum,
      ratingText: r.ratingText || r.starRating || null,
      comment: (r.comment || '').toString(),

      hasReply: !!r.reviewReply?.comment,
      replyLatencyHours: (replyMs && createdMs) ? Math.round(((replyMs - createdMs)/36e5)*100)/100 : null,

      // window
      startISO, endISO, weeks, is12w: true,

      // sentiment
      sentiment_label,
      sentimentCategory: sentiment_label,
    }
  });
}

return out;

// Map + categorize Google Reviews (LAST WEEK)
// Depends on:
//  - "Set Company last week reviews" (company index)
//  - "Build last week window" (startISO/endISO are last week)

const companies = $items('Set Company last week reviews') || [];
if (!companies.length) throw new Error('Set Company last week reviews not found or empty');

const win = ($items('Build last week window')?.[0]?.json) || {};
const week_start = win.startISO?.slice(0,10) || win.week_start || null;
const week_end   = win.endISO?.slice(0,10)   || win.week_end   || null;

// Build locationId -> companyName index
const companyIndex = new Map();
for (const {json:c} of companies) {
  const locId = (c.locationPath || '').split('/').pop();
  if (locId) companyIndex.set(String(locId), c.companyName || c.company || '(unknown)');
}

// Helpers
function toNum(star){
  const m = { ONE:1, TWO:2, THREE:3, FOUR:4, FIVE:5 };
  return typeof star === 'number' ? star : (m[String(star||'').toUpperCase()] ?? 0);
}
function normIso(s){ return s ? String(s).replace(/\.(\d{3})\d*Z$/, '.$1Z') : null; }
function msSafe(s){ const t = s ? Date.parse(normIso(s)) : NaN; return Number.isFinite(t) ? t : null; }
function within(ms){
  const startMs = msSafe(week_start+'T00:00:00Z');
  const endMs   = msSafe(week_end  +'T23:59:59Z'); // inclusive
  return Number.isFinite(ms) && Number.isFinite(startMs) && Number.isFinite(endMs) && ms >= startMs && ms <= endMs;
}
function classify(n){
  const x = Number(n || 0);
  if (!Number.isFinite(x) || x <= 0) return null;
  if (x === 3) return 'Neutral';
  if (x >= 4) return 'Positive';
  if (x <= 2) return 'Negative';
  return null;
}

const out = [];
for (const {json:r} of $input.all()){
  // IDs
  let locationId = r.locationId ? String(r.locationId) : null;
  let reviewId   = r.reviewId   ? String(r.reviewId)   : null;

  if ((!locationId || !reviewId) && r.name) {
    const p = String(r.name).split('/');
    const li = p.indexOf('locations'); if (li > -1 && p[li+1]) locationId ||= p[li+1];
    const ri = p.indexOf('reviews');   if (ri > -1 && p[ri+1]) reviewId   ||= p[ri+1];
  }

  const createdMs = msSafe(r.createTime || r.created);
  if (!within(createdMs)) continue; // keep only last-week reviews

  const replyMs = msSafe(r.reviewReply?.updateTime);
  const ratingNum = Number(r.ratingNum || toNum(r.starRating) || 0);
  const sentiment_label = classify(ratingNum);

  const companyName = r.companyName || companyIndex.get(locationId) || '(unknown)';

  out.push({
    json: {
      companyName,
      locationId,
      reviewId,
      createTime: createdMs ? new Date(createdMs).toISOString() : null,

      ratingNum,
      ratingText: r.ratingText || r.starRating || null,
      comment: (r.comment || '').toString(),

      hasReply: !!r.reviewReply?.comment,
      replyLatencyHours: (replyMs && createdMs) ? Math.round(((replyMs - createdMs)/36e5)*100)/100 : null,

      // weekly fields for grouping
      week_start,
      week_end,
      isLastWeek: true,                 // <â€” tag for Join All

      // sentiment
      sentiment_label,
      sentimentCategory: sentiment_label,

      // stable key if you need it
      key: [companyName, locationId || 'noLocation', week_start || 'noStart'].join('__'),
    }
  });
}

return out;

// Weekly Reviews Summary (LAST WEEK) â€” input: mapped reviews from the node above
function avg(a){ return a.length ? +(a.reduce((x,y)=>x+y,0)/a.length).toFixed(2) : 0; }
function median(a){
  if (!a.length) return 0;
  const s=[...a].sort((x,y)=>x-y), m=Math.floor(s.length/2);
  return s.length%2 ? s[m] : +(((s[m-1]+s[m])/2).toFixed(2));
}

