// Build 12 full weeks ending yesterday (UTC), aligned to whole days.
// Output is a single item with {startISO,endISO,week_start,week_end,weeks:12}

const WEEKS = 12;

function atUtcMidnight(d = new Date()){
  return new Date(Date.UTC(d.getUTCFullYear(), d.getUTCMonth(), d.getUTCDate()));
}

const todayMid = atUtcMidnight();          // today 00:00Z
const endISOd  = new Date(todayMid);       // yesterday 00:00Z
endISOd.setUTCDate(endISOd.getUTCDate() - 1);

// inclusive range: start = end - (7*WEEKS - 1) days
const startISOd = new Date(endISOd);
startISOd.setUTCDate(startISOd.getUTCDate() - (7*WEEKS - 1));

const isoDate = d => d.toISOString().slice(0,10);

return [{
  json: {
    weeks: WEEKS,
    startISO: startISOd.toISOString(),
    endISO:   endISOd.toISOString(),
    week_start: isoDate(startISOd),
    week_end:   isoDate(endISOd),
    ts: {
      start: startISOd.getTime(),
      end:   endISOd.getTime() + 24*60*60*1000 - 1,
    }
  }
}];

// n8n Code node (JavaScript)
// Input: items[] from Join (one per company) with keys:
// impressions, impressions_lastWeek, impressions_12w
// reviews, reviews_lastWeek, reviews_12w
// Optional: allTimeReviews
// Output: same items, enriched with deltas + LLM one-liner prompts

function toNum(x, def = 0) {
  const n = Number(x);
  return Number.isFinite(n) ? n : def;
}
function round(n, dp = 0) {
  const f = Math.pow(10, dp);
  return Math.round(toNum(n) * f) / f;
}
function safePct(numerator, denominator) {
  numerator = toNum(numerator);
  denominator = toNum(denominator);
  if (denominator === 0) return 0;
  return (numerator / denominator) * 100;
}
function pctDelta(thisVal, prevVal) {
  // percent change vs previous: (this - prev) / prev * 100
  const a = toNum(thisVal), b = toNum(prevVal);
  if (b === 0) return 0;
  return ((a - b) / b) * 100;
}
function ppDelta(thisPct, prevPct) {
  return toNum(thisPct) - toNum(prevPct);
}
function hoursDelta(thisH, prevH) {
  return toNum(thisH) - toNum(prevH);
}
function fmtPct(x) { return `${round(x, 0)}%`; }
function fmtPp(x) { const s = round(x, 1); return (s >= 0 ? `+` : ``) + `${s} pp`; }
function fmtHrs(x) { const s = round(x, 1); return `${s}h`; }

// Treat small changes as flat for readability
const FLAT_PCT = 2;     // Â±2% considered "flat" for volumes
const FLAT_PP  = 0.5;   // Â±0.5 pp considered "flat" for rates
const FLAT_HRS = 0.3;   // Â±0.3h considered "flat" for med reply hours

function flatLabel(value, kind = 'pct') {
  const v = toNum(value);
  const th = kind === 'pp' ? FLAT_PP : kind === 'hrs' ? FLAT_HRS : FLAT_PCT;
  if (Math.abs(v) <= th) return 'flat';
  return v > 0 ? 'up' : 'down';
}

function sanitizeBlocks(i) {
  // Provide safe defaults if any blocks are missing
  i.impressions = i.impressions || {};
  i.impressions_lastWeek = i.impressions_lastWeek || {};
  i.impressions_12w = i.impressions_12w || {};
  i.reviews = i.reviews || {};
  i.reviews_lastWeek = i.reviews_lastWeek || {};
  i.reviews_12w = i.reviews_12w || {};
  i.allTimeReviews = i.allTimeReviews || {};
  return i;
}

function buildOutputs(it) {
  const company = it.companyName || it.company || 'Company';

  // ---- Impressions (this, last, 12w) ----
  const imprThis = toNum(it.impressions.total);
  const actThis  = toNum(it.impressions.actions_total);
  const ctrThis  = toNum(it.impressions.ctr_actions_pct);

  const imprLW = toNum(it.impressions_lastWeek.total);
  const actLW  = toNum(it.impressions_lastWeek.actions_total);
  const ctrLW  = toNum(it.impressions_lastWeek.ctr_actions_pct);

  const imprAvg = toNum(it.impressions_12w.avg_week_total);
  const actAvg  = toNum(it.impressions_12w.avg_week_actions_total);
  const ctrAvg  = toNum(it.impressions_12w.avg_week_ctr_pct);

  // deltas vs last week
  const imprWoW = pctDelta(imprThis, imprLW);
  const actWoW  = pctDelta(actThis, actLW);
  const ctrWoW  = ppDelta(ctrThis, ctrLW);

  // deltas vs 12w average
  const imprVsAvg = pctDelta(imprThis, imprAvg);
  const actVsAvg  = pctDelta(actThis, actAvg);
  const ctrVsAvg  = ppDelta(ctrThis, ctrAvg);

  // ---- Reviews (this, last, 12w) ----
  const revThis = toNum(it.reviews.total);
  const posThis = toNum(it.reviews.positiveShare);
  const repThis = toNum(it.reviews.replyRatePct);
  const hrsThis = toNum(it.reviews.medianReplyLatencyHours);

  const revLW   = toNum(it.reviews_lastWeek.total);
  const posLW   = toNum(it.reviews_lastWeek.positiveShare);
  const repLW   = toNum(it.reviews_lastWeek.replyRatePct);
  const hrsLW   = toNum(it.reviews_lastWeek.medianReplyLatencyHours);

  const revAvg  = toNum(it.reviews_12w.avg_total);
  const posAvg  = toNum(it.reviews_12w.avg_positiveShare);
  const repAvg  = toNum(it.reviews_12w.avg_replyRatePct);
  const hrsAvg  = toNum(it.reviews_12w.avg_medianReplyLatencyHours);

  // Review deltas
  const revWoW = pctDelta(revThis, revLW);
  const revVsAvg = pctDelta(revThis, revAvg);
  const posWoWpp = ppDelta(posThis, posLW);
  const posVsAvgpp = ppDelta(posThis, posAvg);
  const repWoWpp = ppDelta(repThis, repLW);
  const repVsAvgpp = ppDelta(repThis, repAvg);
  const hrsWoW = hoursDelta(hrsThis, hrsLW);
  const hrsVsAvg = hoursDelta(hrsThis, hrsAvg);

  // ---- KPI chip text (optional) ----
  it.kpi = {
    impressions_vs12w_pct: round(imprVsAvg, 0),   // negative is below avg
    actions_vs12w_pct:     round(actVsAvg, 0),
    ctr_vs12w_pp:          round(ctrVsAvg, 1),
    reviews_vs12w_count:   round(revThis - revAvg, 1) // absolute difference in count vs avg
  };

  // ---- LLM one-liner prompts ----
  it.oneLinerOverall_prompt =
`Write ONE sentence (â‰¤26 words) on ${company}'s weekly Google performance vs last week and vs 12-week average.
Numbers (whole %; CTR deltas in pp):
This: ${imprThis} impr, ${actThis} actions, CTR ${round(ctrThis,0)}%.
Last: ${imprLW} impr, ${actLW} actions, CTR ${round(ctrLW,0)}%.
12w: ${round(imprAvg,0)} impr, ${round(actAvg,0)} actions, CTR ${round(ctrAvg,0)}%.
Reviews: this ${revThis} (${round(posThis,0)}% positive), last ${revLW}, 12w avg ${round(revAvg,1)}.
Output only the sentence.`;

  it.oneLinerImpressions_prompt =
`One sentence (â‰¤22 words) comparing impressions, actions, and CTR this week vs last week and the 12-week average; say up/down/flat in whole % or "pp".
This: ${imprThis} impr, ${actThis} actions, CTR ${round(ctrThis,0)}%.
Last: ${imprLW} impr, ${actLW} actions, CTR ${round(ctrLW,0)}%.
12w: ${round(imprAvg,0)} impr, ${round(actAvg,0)} actions, CTR ${round(ctrAvg,0)}%.`;

  it.oneLinerReviews_prompt =
`One sentence (â‰¤22 words) comparing review volume, sentiment (positive share), and reply behavior this week vs last week and 12-week average.
This: ${revThis} total, ${round(posThis,0)}% positive, replies ${round(repThis,0)}%, median ${round(hrsThis,1)}h.
Last: ${revLW} total, ${round(posLW,0)}% positive, replies ${round(repLW,0)}%, median ${round(hrsLW,1)}h.
12w: ${round(revAvg,1)} avg/wk, ${round(posAvg,0)}% positive, replies ${round(repAvg,0)}%, median ${round(hrsAvg,1)}h.`;

  // ---- Add friendly deltas for chips/labels (already rounded) ----
  it.deltas = {
    // vs last week
    impr_wow_pct: round(imprWoW, 0),
    act_wow_pct:  round(actWoW, 0),
    ctr_wow_pp:   round(ctrWoW, 1),
    // vs 12w avg
    impr_vsavg_pct: round(imprVsAvg, 0),
    act_vsavg_pct:  round(actVsAvg, 0),
    ctr_vsavg_pp:   round(ctrVsAvg, 1),
    // reviews
    reviews_wow_pct: round(revWoW, 0),
    reviews_vsavg_pct: round(revVsAvg, 0),
    pos_wow_pp: round(posWoWpp, 1),
    pos_vsavg_pp: round(posVsAvgpp, 1),
    replies_wow_pp: round(repWoWpp, 1),
    replies_vsavg_pp: round(repVsAvgpp, 1),
    replyHours_wow: round(hrsWoW, 1),
    replyHours_vsavg: round(hrsVsAvg, 1)
  };

  // ---- Flat flags (optional, for â€œ=â€ badges) ----
  it.flags = {
    impr_wow_flat: flatLabel(imprWoW, 'pct') === 'flat',
    act_wow_flat:  flatLabel(actWoW, 'pct') === 'flat',
    ctr_wow_flat:  flatLabel(ctrWoW, 'pp')  === 'flat',
    impr_vsavg_flat: flatLabel(imprVsAvg, 'pct') === 'flat',
    act_vsavg_flat:  flatLabel(actVsAvg, 'pct') === 'flat',
    ctr_vsavg_flat:  flatLabel(ctrVsAvg, 'pp')  === 'flat',
    reviews_wow_flat: flatLabel(revWoW, 'pct') === 'flat',
    reviews_vsavg_flat: flatLabel(revVsAvg, 'pct') === 'flat',
    pos_wow_flat: flatLabel(posWoWpp, 'pp') === 'flat',
    pos_vsavg_flat: flatLabel(posVsAvgpp, 'pp') === 'flat',
    replies_wow_flat: flatLabel(repWoWpp, 'pp') === 'flat',
    replies_vsavg_flat: flatLabel(repVsAvgpp, 'pp') === 'flat',
    replyHours_wow_flat: flatLabel(hrsWoW, 'hrs') === 'flat',
    replyHours_vsavg_flat: flatLabel(hrsVsAvg, 'hrs') === 'flat'
  };

  // Convenience fields for HTML bindings (this/last/avg, rounded for display)
  it.display = {
    impressions: {
      this: { impr: imprThis, act: actThis, ctr: round(ctrThis,0) },
      last: { impr: imprLW,   act: actLW,   ctr: round(ctrLW,0) },
      avg:  { impr: round(imprAvg,0), act: round(actAvg,0), ctr: round(ctrAvg,0) }
    },
    reviews: {
      this: { total: revThis, pos: round(posThis,0), replies: round(repThis,0), hours: round(hrsThis,1) },
      last: { total: revLW,   pos: round(posLW,0),   replies: round(repLW,0),   hours: round(hrsLW,1) },
      avg:  { total: round(revAvg,1), pos: round(posAvg,0), replies: round(repAvg,0), hours: round(hrsAvg,1) }
    }
  };

  return it;
}

const out = [];
for (const item of items) {
  const i = sanitizeBlocks({ ...item.json });
  out.push({ json: buildOutputs(i) });
}
return out;

// Input item shape from Merge One Liners:
// { output: { companyKey: "Company__locationId", oneLinerOverall?, oneLinerImpressions?, oneLinerReviews? } }

const items = $input.all();

function nz(v){ return v == null ? "" : String(v).trim(); }

function pickLine(o){
  // prefer Overall, fallback to Impressions, then Reviews
  return nz(o.oneLinerOverall || o.oneLinerImpressions || o.oneLinerReviews || "");
}

return items.map(({json}) => {
  const out = json.output || {};
  const key = nz(out.companyKey);
  const parts = key.split("__");               // ["Company", "locationId"]
  const company = nz(parts[0] || "");
  const locationId = nz(parts[1] || "");
  const one_liner = pickLine(out);

  return {
    json: {
      company,
      locationId,
      // if you also carry accountId elsewhere, you can add it here
      join_key: key,            // use the exact companyKey to avoid name mismatches
      one_liner
    }
  };
});

// Build join_key = company + "__" + locationId (strings), and rename headers.

const rows = $items('Read Recipients').map(i => i.json || {});
function nz(v){ return v == null ? "" : String(v).trim(); }

return rows.map(r => {
  const company    = nz(r.company);
  const accountId  = nz(r.accountId);
  const locationId = nz(r.locationId);
  const email_to   = nz(r["Email to"]);
  const email_cc   = nz(r["Email CC"]);
  const slack_channel = nz(r.slack_channel);
  const slack_user    = nz(r.slack_user);

  // match upstream join_key exactly (Company__locationId)
  const join_key = company + "__" + locationId;

  return {
