      company, accountId, locationId, join_key,
      email_to, email_cc, slack_channel, slack_user
    }
  };
});

// Input: mixed items per join_key (may be structured as output.oneLinerOverall/Impressions/Reviews,
// or flat "one_liner") + recipient fields.
// Also reads from node "format" to attach week_start/week_end by locationId.
// Output: exactly 1 item per join_key with lines.{overall,impressions,reviews} (no nulls),
// one_liner (joined), week_start, week_end, period, and recipient fields.

// ---------- helpers ----------
const S = v => String(v ?? '').trim();

function fallbackClassify(text = '') {
  // only used if we don't have explicit labels
  const t = S(text).toLowerCase();
  if (t.includes('impressions') || t.includes('ctr') || t.includes('actions')) return 'impressions';
  if (t.includes('review') || t.includes('sentiment') || t.includes('reply') || t.includes('rating')) return 'reviews';
  return 'overall';
}

// Build a reliable join key from the item
function getJoinKey(j) {
  // Prefer explicit join_key
  if (S(j.join_key)) return S(j.join_key);

  // If structured output exists, it usually has companyKey like "<name>__<locationId>"
  if (j.output && S(j.output.companyKey)) return S(j.output.companyKey);

  // As a last resort, compose
  const c = S(j.company);
  const loc = S(j.locationId);
  return c && loc ? `${c}__${loc}` : '';
}

// ---------- build lookup from "format" by locationId ----------
const fmtArray = ($items('format', 0, 0) || []).map(i => i.json || {});
const fmtByLoc = new Map(fmtArray.map(f => [S(f.locationId), f]));

// ---------- group ----------
const groups = new Map();

for (const { json } of $input.all()) {
  // Determine grouping key
  const key = getJoinKey(json);
  if (!key) continue;

  // Start/restore group
  const g = groups.get(key) || {
    join_key: key,
    company: S(json.company) || S((json.output || {}).companyName) || S(key.split('__')[0]),
    locationId: S(json.locationId) || S((json.output || {}).locationId) || S(key.split('__')[1]),
    lines: { overall: null, impressions: null, reviews: null }
  };

  // --- Bring in recipients/identity if present
  g.accountId      = g.accountId      ?? json.accountId;
  g.email_to       = g.email_to       ?? json.email_to;
  g.email_cc       = g.email_cc       ?? json.email_cc;
  g.slack_channel  = g.slack_channel  ?? json.slack_channel;
  g.slack_user     = g.slack_user     ?? json.slack_user;

  // --- Prefer structured one-liners when available
  if (json.output) {
    const o = json.output;
    if (S(o.oneLinerOverall))     g.lines.overall     = S(o.oneLinerOverall);
    if (S(o.oneLinerImpressions)) g.lines.impressions = S(o.oneLinerImpressions);
    if (S(o.oneLinerReviews))     g.lines.reviews     = S(o.oneLinerReviews);
  }

  // --- If we only have a flat one_liner, classify and place
  if (!json.output && S(json.one_liner)) {
    const line = S(json.one_liner);
    const cls = fallbackClassify(line);
    if (!g.lines[cls]) g.lines[cls] = line;
    else if (!g.lines.overall) g.lines.overall = line;           // spillover
    else if (!g.lines.overall.includes(line)) g.lines.overall += ' ' + line;
  }

  // --- Attach week period from "format" by locationId
  const f = fmtByLoc.get(S(g.locationId)) || {};
  g.week_start = g.week_start || f.week_start || '';
  g.week_end   = g.week_end   || f.week_end   || '';
  g.period     = (S(g.week_start) && S(g.week_end)) ? `week period = ${g.week_start}â€“${g.week_end}` : '';

  groups.set(key, g);
}

// ---------- finalize: ensure no nulls and stable order ----------
const out = [];
for (const g of groups.values()) {
  // Fill any missing slots with sensible fallbacks
  // If overall missing, derive from impressions + reviews; else mark em-dash.
  if (!g.lines.overall) {
    if (g.lines.impressions && g.lines.reviews) {
      g.lines.overall = `${g.lines.impressions} ${g.lines.reviews}`;
    } else if (g.lines.impressions) {
      g.lines.overall = g.lines.impressions;
    } else if (g.lines.reviews) {
      g.lines.overall = g.lines.reviews;
    } else {
      g.lines.overall = 'â€”';
    }
  }
  if (!g.lines.impressions) g.lines.impressions = 'â€”';
  if (!g.lines.reviews)     g.lines.reviews     = 'â€”';

  // Join in this order: overall â†’ impressions â†’ reviews
  const oneJoined = [g.lines.overall, g.lines.impressions, g.lines.reviews]
    .filter(s => S(s).length > 0)
    .join('\n');

  out.push({ json: { ...g, one_liner: oneJoined } });
}

return out;
