// filename: n8n-code-node-attrition-email.js
// This Code node builds an email for each item indicating high attrition risk.
// Output fields:
//   - emailSubject
//   - emailBody
//   - emailTo (optional if you want to set here)
//   - emailCc (optional)
//   - emailMetadata (structured data for downstream logging)

function formatDate(dateStr) {
  if (!dateStr) return "N/A";
  const d = new Date(dateStr);
  if (isNaN(d.getTime())) return dateStr;
  // Format as YYYY-MM-DD for clarity
  return `${d.getFullYear()}-${String(d.getMonth() + 1).padStart(2, "0")}-${String(d.getDate()).padStart(2, "0")}`;
}

function clampScore(score) {
  const n = Number(score);
  if (Number.isNaN(n)) return null;
  return Math.max(0, Math.min(100, n));
}

function classifyRisk(score) {
  if (score == null) return "Unknown";
  if (score >= 80) return "High";
  if (score >= 50) return "Medium";
  return "Low";
}

function bulletList(items) {
  if (!Array.isArray(items) || items.length === 0) return "- None";
  return items.map(s => `- ${String(s).trim() || "N/A"}`).join("\n");
}

const outputs = [];

for (const item of $input.all()) {
  const j = item.json || {};

  const personName = j.personName || j.name || "The employee";
  const role = j.role || "N/A";
  const department = j.department || null;
  const managerName = j.managerName || "HR/People Ops";
  const riskScoreRaw = j.riskScore ?? j.attritionRiskScore;
  const riskScore = clampScore(riskScoreRaw);
  const riskLevel = classifyRisk(riskScore);
  const signals = Array.isArray(j.signals) ? j.signals : [];
  const lastEngagementDate = formatDate(j.lastEngagementDate || j.lastCheckInDate);
  const recommendedActions = Array.isArray(j.recommendedActions) ? j.recommendedActions : [
    "Schedule a 1:1 check‚Äëin within the next 3‚Äì5 days",
    "Offer growth or role‚Äëclarity conversation",
    "Review workload and compensation alignment",
  ];

  // Email routing (optional; you can also set these in the Email node)
  const emailTo = j.emailTo || j.managerEmail || j.hrEmail || "";
  const emailCc = j.emailCc || "";

  // Compose subject
  const subjectParts = [
    "[Attrition Alert]",
    personName !== "The employee" ? personName : "Employee",
    riskLevel !== "Unknown" ? `‚Äì ${riskLevel} Risk (${riskScore}%)` : "‚Äì Risk Review",
  ];
  const emailSubject = subjectParts.filter(Boolean).join(" ");

  // Compose body (plain text; you can convert to HTML if preferred)
  const headerLine = `${personName} ${role !== "N/A" ? `(${role})` : ""}${department ? `, ${department}` : ""}`;
  const riskLine = `Attrition Risk: ${riskLevel}${riskScore != null ? ` (${riskScore}%)` : ""}`;
  const signalsBlock = bulletList(signals);
  const actionsBlock = bulletList(recommendedActions);

  const emailBody =
`Hi ${managerName},

This is a heads‚Äëup that ${headerLine} may be at risk of leaving soon. ${riskLine}.

Key signals observed:
${signalsBlock}

Last engagement/check‚Äëin: ${lastEngagementDate}

Suggested next steps:
${actionsBlock}

Please prioritize a supportive outreach. If helpful, we can prepare a retention plan (growth discussion, workload review, recognition, and role clarity).

Thanks,
People Analytics
`;

  outputs.push({
    json: {
      ...j,
      emailSubject,
      emailBody,
      emailTo,
      emailCc,
      emailMetadata: {
        personName,
        role,
        department,
        managerName,
        riskScore,
        riskLevel,
        signals,
        lastEngagementDate,
        recommendedActions,
        generatedAt: new Date().toISOString(),
      },
    },
  });
}

return outputs;
