// Récupérer la réponse HTTP brute
const response = $input.first().json;
const results = response.results || [];

// Fonction pour formater la date en dd/MM/yyyy
function formatDate(dateString) {
  if (!dateString) return null;

  try {
    const date = new Date(dateString);
    const day = date.getDate().toString().padStart(2, '0');
    const month = (date.getMonth() + 1).toString().padStart(2, '0');
    const year = date.getFullYear();
    return `${day}/${month}/${year}`;
  } catch (error) {
    return dateString;
  }
}

// Fonction pour générer l'URL du PDF
function generatePdfUrl(urlAvis, dateParution) {
  if (!urlAvis || !dateParution) return null;

  try {
    // Extraire l'idweb de l'URL d'annonce
    const match = urlAvis.match(/idweb:(%22)?([^%"&]+)(%22)?/);
    if (!match) return null;

    const idweb = match[2]; // 25-102635

    // Extraire le mois de la date de parution
    const date = new Date(dateParution);
    const month = (date.getMonth() + 1).toString().padStart(2, '0');
    const year = date.getFullYear();

    // Construire l'URL PDF
    return `https://www.boamp.fr/telechargements/FILES/PDF/${year}/${month}/${idweb}.pdf`;

  } catch (error) {
    return null;
  }
}

// Créer un item séparé pour chaque appel d'offre
return results.map(item => ({
  json: {
    "Parution": item.dateparution,
    "Objet": item.objet,
    "URL": generatePdfUrl(item.url_avis, item.dateparution),
    "Acheteur": item.nomacheteur,
    "Date limite": formatDate(item.datelimitereponse)
  }
}));

// Récupération des mots-clés depuis l'objet
const keywordData = $('Get keyword').first().json['Mot clé'];
const keywordsString = keywordData[0]['Mots clés'] || '';

// Transformation de la chaîne en tableau et nettoyage
const termesToSearch = keywordsString
  .split(',')
  .map(term => term.trim()) // Supprime les espaces avant/après
  .filter(term => term.length > 0); // Supprime les éléments vides

// Récupération du texte d'entrée depuis le champ "text" de la node précédente
const inputText = $input.first().json.text || '';

// Fonction pour normaliser le texte (supprime accents, met en minuscules)
function normalizeText(text) {
  return text
    .toLowerCase()
    .normalize('NFD')
    .replace(/[\u0300-\u036f]/g, ''); // Supprime les accents
}

// Normalisation du texte d'entrée
const normalizedInput = normalizeText(inputText);

// Recherche des correspondances
const foundTerms = [];
let hasMatch = false;

termesToSearch.forEach(term => {
  const normalizedTerm = normalizeText(term);

  // Recherche du terme exact ou avec variations
  if (normalizedInput.includes(normalizedTerm)) {
    foundTerms.push(term);
    hasMatch = true;
  }
});

// Construction du résultat
const result = {
  hasMatch: hasMatch,
  foundTerms: foundTerms,
  totalMatches: foundTerms.length,
  searchedTerms: termesToSearch,
  originalText: inputText.substring(0, 200) + (inputText.length > 200 ? '...' : '') // Extrait pour debug
};

// Ajout de détails sur les correspondances trouvées
if (hasMatch) {
  result.matchDetails = foundTerms.map(term => {
    const normalizedTerm = normalizeText(term);
    const index = normalizedInput.indexOf(normalizedTerm);
    return {
      term: term,
      position: index,
      context: inputText.substring(Math.max(0, index - 50), index + term.length + 50)
    };
  });
}

return [{ json: result }];

