// Récupération de la configuration depuis le node "Get config"
const configData = $node["Get config"].json;
console.log("Configuration:", JSON.stringify(configData, null, 2));

// Extraction des types de marché activés (checkbox à true) depuis la config
let marketTypes = [];

if (Array.isArray(configData)) {
    // Si c'est un tableau, on prend le premier élément
    const config = configData[0];
    if (config.Travaux === true) marketTypes.push("TRAVAUX");
    if (config.Services === true) marketTypes.push("SERVICES");
    if (config.Fournitures === true) marketTypes.push("FOURNITURES");
} else {
    // Si c'est un objet unique
    if (configData.Travaux === true) marketTypes.push("TRAVAUX");
    if (configData.Services === true) marketTypes.push("SERVICES");
    if (configData.Fournitures === true) marketTypes.push("FOURNITURES");
}

console.log("Types de marché à filtrer:", marketTypes);

// Vérification qu'au moins un type est sélectionné
if (marketTypes.length === 0) {
    throw new Error("Aucun type de marché sélectionné dans la configuration");
}

// Récupération des données depuis l'input
const inputData = $input.all();

// Debug : afficher la structure des données
console.log("inputData:", JSON.stringify(inputData, null, 2));

// Vérification que les données sont bien structurées
if (!inputData || inputData.length === 0) {
    throw new Error("Aucune donnée en entrée");
}

// Récupération du premier item
const firstItem = inputData[0];
console.log("firstItem:", JSON.stringify(firstItem, null, 2));

const data = firstItem.json;
console.log("data:", JSON.stringify(data, null, 2));

// La structure semble être directement un tableau avec total_count et results
let payload;
if (Array.isArray(data)) {
    payload = data[0];
} else if (data && data.total_count !== undefined && data.results) {
    payload = data;
} else {
    throw new Error("Structure de données non reconnue");
}

console.log("payload:", JSON.stringify(payload, null, 2));

if (!payload.results || !Array.isArray(payload.results)) {
    throw new Error("Structure de données invalide - results non trouvé");
}

// Filtrage des résultats pour ne garder que les appels d'offres correspondant aux types de la config
const filteredResults = payload.results.filter(item => {
    if (!item.type_marche || !Array.isArray(item.type_marche)) {
        return false;
    }

    // Vérifie si au moins un des types de marché de l'item correspond à ceux de la config
    return item.type_marche.some(type => 
        marketTypes.includes(type)
    );
});

// Construction du tableau de sortie avec la même structure
const output = [{
    total_count: payload.total_count, // Garde le total_count original
    filtered_count: filteredResults.length, // Ajoute le nombre filtré
    market_types_used: marketTypes, // Ajoute les types utilisés pour le filtre
    results: filteredResults
}];

// Log pour debug
console.log(`Nombre d'appels d'offres filtrés: ${filteredResults.length} sur ${payload.total_count} total`);
console.log(`Types de marché filtrés: ${marketTypes.join(', ')}`);

// Retour du résultat
return output;
