const fs = require('fs');
const lastPath = '/.n8n/rss_last_posted.txt';
const tempPath = '/.n8n/rss_temp_url.txt';

return items.map(item => {
  let linkHash = item.json.linkHash || '';

  // Fallback: Read from temp file if linkHash is missing
  if (!linkHash && fs.existsSync(tempPath)) {
    try {
      linkHash = fs.readFileSync(tempPath, 'utf8').trim();
      console.log("Read linkHash from temp file:", linkHash);
    } catch (error) {
      console.error("Failed to read temp file:", error.message);
    }
  }

  if (linkHash) {
    try {
      fs.writeFileSync(lastPath, linkHash, 'utf8');
      console.log("Saved linkHash to rss_last_posted.txt:", linkHash);
    } catch (error) {
      console.error("Failed to save linkHash:", error.message);
    }
  } else {
    console.log("No linkHash available to save.");
  }

  return item;
});

function simpleHash(str) {
  let hash = 0;
  for (let i = 0; i < str.length; i++) {
    const char = str.charCodeAt(i);
    hash = ((hash << 5) - hash) + char;
    hash = hash & hash; // Convert to 32-bit integer
  }
  return hash.toString(16);
}

return items.map(item => {
  const link = item.json.link || '';
  const linkHash = simpleHash(link.trim()); // Trim to remove trailing spaces
  item.json.linkHash = linkHash;
  console.log("Calculated linkHash:", linkHash);
  return item;
});

const fs = require('fs');
const logPath = '/.n8n/debug_log.txt'; // Path to the debug log file
const lastPath = '/.n8n/rss_last_posted.txt';

return items.map(item => {
  const linkHash = item.json.linkHash || '';
  let lastPostedHash = '';
  try {
    if (fs.existsSync(lastPath)) {
      lastPostedHash = fs.readFileSync(lastPath, 'utf8').trim();
      console.log("Comparison - linkHash:", linkHash, "lastPostedHash:", lastPostedHash, "File Exists:", fs.existsSync(lastPath));
    } else {
      console.log("Comparison - File does not exist:", lastPath);
    }
  } catch (error) {
    console.error("Failed to read last posted hash:", error.message);
  }
  const shouldProceed = linkHash !== lastPostedHash;
  item.json.shouldProceed = shouldProceed;
  console.log("Comparison - shouldProceed:", shouldProceed, "Execution ID:", $execution.id);

  // Log to file
  try {
    const logData = {
      timestamp: new Date().toISOString(),
      linkHash,
      lastPostedHash,
      shouldProceed
    };
    fs.appendFileSync(logPath, JSON.stringify(logData) + '\n');
    console.log("Logged to debug_log.txt");
  } catch (error) {
    console.error("Failed to write to debug_log.txt:", error.message);
  }

  return item;
