const item = $input.item.json;
const upload = $('Upload Image to Postiz').item.json;
console.log('Input validation:', { text: item.text, link: item.link, imageId: upload.id, imagePath: upload.path });
if (!item.text || !item.link || !upload.id || !upload.path) {
  throw new Error(`Invalid input: text=${item.text}, link=${item.link}, image.id=${upload.id}, image.path=${upload.path}`);
}
return $input.item;

function simpleHash(str) {
  let hash = 0;
  for (let i = 0; i < str.length; i++) {
    const char = str.charCodeAt(i);
    hash = ((hash << 5) - hash) + char;
    hash = hash & hash;
  }
  return hash.toString(16);
}

return items.map(item => {
  const inputText = item.json.text || "No summary available";
  const maxChars = item.json.maxChars || 200;
  let text = inputText;
  let link = item.json.link || "";
  let hashtags = [];
  console.log("Input text:", inputText);
  const parts = inputText.split(" ").filter(part => part.trim());
  const lastPart = parts[parts.length - 1];
  if (lastPart && lastPart.includes("http")) {
    link = lastPart;
    hashtags = parts.slice(-4, -1).filter(part => part.startsWith("#"));
    text = parts.slice(0, -4).join(" ");
  } else if (parts.length >= 4) { // Fallback if link not detected
    hashtags = parts.slice(-3).filter(part => part.startsWith("#"));
    text = parts.slice(0, -3).join(" ");
  }
  if (hashtags.length !== 3) {
    console.warn(`Expected 3 hashtags, got ${hashtags.length}. Using defaults if needed.`);
    hashtags = ["#Wellbeing", "#Health", "#Inspiration"]; // Fallback
  }
  text = text.replace(/\n/g, " ").replace(/"/g, '\\"').trim().slice(0, maxChars);
  const linkHash = simpleHash(link);
  console.log("Processed - text:", text, "hashtags:", hashtags, "link:", link, "length:", (text + " " + hashtags.join(" ") + " " + link).length);
  return {
    json: {
      text: text,
      hashtags: hashtags,
      link: link,
      linkHash: linkHash,
      title: item.json.title || text.slice(0, 100),
      imageId: item.json.id || null
    }
  };
});

const fs = require('fs');
