const items = $input.all();

// Group by email
const grouped = {};

for (const item of items) {
  const { email, campaignId, NocoDB_id } = item.json;

  if (!grouped[email]) {
    grouped[email] = {
      email,
      NocoDB_id,
      messagesSent: []
    };
  }

  grouped[email].messagesSent.push(campaignId);
}

// Convert grouped object to array
return Object.values(grouped).map(entry => ({
  json: {
    email: entry.email,
    NocoDB_id: entry.NocoDB_id,
    messagesSent: entry.messagesSent.join(', ')
  }
}));

