const items = $input.all();

// Group by email
const grouped = {};

for (const item of items) {
  const { email, NocoDB_id, messagesSent, delivered, opened, clicked } = item.json;

  if (!grouped[email]) {
    grouped[email] = {
      email,
      NocoDB_id,
      messagesSent: [],
      delivered: [],
      opened: [],
      clicked: []
    };
  }

  if (messagesSent) grouped[email].messagesSent.push(messagesSent);
  if (delivered) grouped[email].delivered.push(delivered);
  if (opened) grouped[email].opened.push(opened);
  if (clicked) grouped[email].clicked.push(clicked);
}

// Merge arrays and clean duplicates if needed
return Object.values(grouped).map(entry => ({
  json: {
    email: entry.email,
    NocoDB_id: entry.NocoDB_id,
    messagesSent: entry.messagesSent.join(', '),
    delivered: entry.delivered.join(', '),
    opened: entry.opened.join(', '),
    clicked: entry.clicked.join(', ')
  }
}));
