// Get the input data from the first item
const inputData = items[0].json;

// This will hold our final list of individual items
const outputItems = [];

const nextCursor = inputData.next_cursor;

// Loop through each tweet in the 'tweets' array from the input
for (const tweet of inputData.tweets) {
  // 1. Create the clean object for a single tweet
  const cleanTweet = {
    id:tweet.id,
    text: tweet.text,
    url: tweet.url,
    authorUsername: tweet.author.userName,
    authorName: tweet.author.name,
    createdAt: tweet.createdAt,
    likeCount: tweet.likeCount,
    retweetCount: tweet.retweetCount,
    replyCount: tweet.replyCount,
    quoteCount: tweet.quoteCount,
    viewCount: tweet.viewCount,
    isReply: tweet.isReply,
    nextCursor: nextCursor 
  };

  if (tweet.isReply) {
    cleanTweet.inReplyToUsername = tweet.inReplyToUsername;
    cleanTweet.inReplyToId = tweet.inReplyToId;
  }

  // 2. Wrap this single tweet object in the required N8N item structure
  const n8nItem = {
    json: cleanTweet
  };

  // 3. Add this new item to our output list
  outputItems.push(n8nItem);
}

// Return the array of individual items
return outputItems;

const items = $input.all();

return items.map(item => {
  // Default to 1 if no counter exists
  let newCount = 1;

  // 1. Correctly check if the counter EXISTS
  if (item.json && item.json.counter != null) {
    // 2. Convert the counter text to a number before adding
    newCount = parseInt(item.json.counter) + 1;
  }

  // Make sure the json object exists
  if (!item.json) {
    item.json = {};
  }

  // Add the new, calculated count to the item
  item.json.count = newCount;

  // 3. Return the single MODIFIED item, not the whole list
  return item;
});

// Get the raw text output and remove any leading/trailing whitespace
const rawOutput = $input.first().json.output.trim();
const outputItems = [];

// 1. Split the entire text into individual analysis blocks using "---"
const analyses = rawOutput.split('\n---\n');

// 2. Loop through each text block
for (const chunk of analyses) {
  // Skip any empty blocks that might result from the split
  if (chunk.trim() === '') continue;

  const lines = chunk.trim().split('\n');
  const cleanAnalysis = {};

  // 3. Loop through each line (e.g., "Sentiment:: Positive")
  for (const line of lines) {
    const parts = line.split('::');
    if (parts.length === 2) {
      // This part cleans up the key, e.g., "Key Topic" becomes "keyTopic"
      let key = parts[0].trim();
      key = key.charAt(0).toLowerCase() + key.slice(1).replace(/\s+(\w)/g, (match, letter) => letter.toUpperCase());

      const value = parts[1].trim();
      cleanAnalysis[key] = value;
    }
  }

  // 4. If we successfully built an object, add it to the output
  if (Object.keys(cleanAnalysis).length > 0) {
    outputItems.push({ json: cleanAnalysis });
  }
}

return outputItems;

// Get the full text output from the AI
const aiOutput = $input.first().json.text;

// Regex to find the JSON code block
const jsonRegex = /```json\n([\s\S]*?)```/;
const match = aiOutput.match(jsonRegex);

// If we found a JSON block, parse it and return each object as a separate item
if (match && match[1]) {
  try {
    const jsonString = match[1];
    const actionableTweets = JSON.parse(jsonString);

    if (Array.isArray(actionableTweets)) {
      // This returns multiple items, one for each urgent tweet
      return actionableTweets.map(tweet => ({ json: tweet }));
    }
  } catch (error) {
    console.log("Failed to parse JSON:", error);
    return []; // Return nothing if JSON is broken
  }
}

// Return nothing if no JSON block was found
return [];

const items = $input.all();
let combinedText = "Here are the recent analyses to summarize:\n\n";
const rowsToUpdate = [];

// Loop through every row from the Google Sheet
for (const item of items) {
  const data = item.json;
  // Add the row number to our list for later
  rowsToUpdate.push({ rowNumber: item.json.rowNumber });

  // Create a neat summary for each analysis
  const entry = `
---
Tweet ID: ${data.tweetId}
Sentiment: ${data.sentiment}
Key Topic: ${data.keyTopic}
Urgency: ${data.urgency}
Summary: ${data.summary}
---
  `;
  combinedText += entry;
}

// Return a SINGLE item with all the data we need
return [{
  json: {
    preparedText: combinedText, // For the AI
    rowsToUpdate: rowsToUpdate  // For the update step
  }
}];

// Get the full text output from the AI
const aiOutput = $input.first().json.text;

// Define the delimiter that separates the summary from the JSON
const summaryDelimiter = "--- ACTIONABLE TWEETS JSON ---";

// Find where the summary text ends
const summaryEndIndex = aiOutput.indexOf(summaryDelimiter);

// The summary is everything before the delimiter. If delimiter isn't found, use the whole text.
const summaryReport = summaryEndIndex !== -1 ? aiOutput.substring(0, summaryEndIndex).trim() : aiOutput.trim();

// Return a single item with just the summary report
return [{ json: { summaryReport: summaryReport } }];
