// Get currently active sprint or the latest one
const lists = $input.all();
const now = Date.now();

// First, try to find active sprints (current time between start and due date)
const activeSprints = lists.filter(list => {
  const startDate = parseInt(list.json.start_date);
  const dueDate = parseInt(list.json.due_date);
  return startDate <= now && now <= dueDate;
});

let latestSprint;

if (activeSprints.length > 0) {
  // If we have active sprints, get the most recent one by start date
  latestSprint = activeSprints.sort((a, b) => 
    parseInt(b.json.start_date) - parseInt(a.json.start_date)
  )[0];
} else {
  // No active sprints, get the most recent by start date (could be future sprint)
  latestSprint = lists.sort((a, b) => 
    parseInt(b.json.start_date) - parseInt(a.json.start_date)
  )[0];
}

return {
  json: {
    latest_sprint_id: latestSprint.json.id,
    latest_sprint_name: latestSprint.json.name,
    latest_sprint_start_date: latestSprint.json.start_date,
    latest_sprint_due_date: latestSprint.json.due_date,
    is_currently_active: activeSprints.length > 0,
    total_sprints_found: lists.length
  }
};

// Get all inputs dynamically
const allInputs = $input.all();

// Extract ClickUp data from all items
const clickupData = allInputs.map(item => item.json).filter(item => item && item.id);

const highPriority = [];
const normalPriority = [];
const blockers = [];
const urgentTasks = [];
const inProgress = [];

clickupData.forEach(task => {
  // Extract checklist items as subtasks
  const subtasks = [];
  if (task.checklists && task.checklists.length > 0) {
    task.checklists.forEach(checklist => {
      if (checklist.items && checklist.items.length > 0) {
        checklist.items.forEach(item => {
          subtasks.push({
            name: item.name,
            resolved: item.resolved,
            assignee: item.assignee ? 'Assigned' : 'Unassigned'
          });
        });
      }
    });
  }

  const taskInfo = {
    name: task.name || 'Untitled',
    status: task.status?.status || 'Unknown',
    statusType: task.status?.type || 'unknown',
    priority: task.priority?.priority || 'normal',
    url: task.url || '',
    assignees: task.assignees?.map(a => a.username).join(', ') || 'Unassigned',
    list: task.list?.name || '',
    folder: task.folder?.name || task.project?.name || '',
    dueDate: task.due_date ? new Date(parseInt(task.due_date)).toLocaleDateString('en-US', { month: 'short', day: 'numeric' }) : 'No due date',
    timeSpent: task.time_spent ? `${Math.floor(task.time_spent / 60000)} min` : null,
    description: task.description ? (task.description.length > 100 ? task.description.substring(0, 100) + '...' : task.description) : null,
    subtasks: subtasks,
    subtasksSummary: subtasks.length > 0 ? `${subtasks.filter(s => s.resolved).length}/${subtasks.length} completed` : null
  };

  // Categorize by status first (in progress)
  if (task.status?.status?.toLowerCase().includes('progress')) {
    inProgress.push(taskInfo);
  }
  // Check for blockers
  else if (task.status?.status?.toLowerCase().includes('block')) {
    blockers.push(taskInfo);
  } 
  // Then by priority
  else if (task.priority?.priority === 'urgent') {
    urgentTasks.push(taskInfo);
  } 
  else if (task.priority?.priority === 'high') {
    highPriority.push(taskInfo);
  } 
  else {
    normalPriority.push(taskInfo);
  }
});

// Dynamically get board URL from first task
const firstTask = clickupData[0];
const boardUrl = firstTask?.list?.id 
  ? `https://app.clickup.com/${firstTask.team_id}/v/li/${firstTask.list.id}`
  : firstTask?.space?.id 
  ? `https://app.clickup.com/${firstTask.team_id}/v/s/${firstTask.space.id}`
  : 'https://app.clickup.com';

// Count status types
const statusSummary = {
  backlogs: clickupData.filter(t => t.status?.status === 'backlogs').length,
  inProgress: clickupData.filter(t => t.status?.status?.toLowerCase().includes('progress')).length,
  completed: clickupData.filter(t => t.status?.type === 'closed').length
};

const summary = {
  date: new Date().toLocaleDateString('en-US', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' }),
  inProgressTasks: inProgress,
  urgentTasks: urgentTasks,
  highPriorityTasks: highPriority,
  normalPriorityTasks: normalPriority,
  blockers: blockers,
  clickupBoardUrl: boardUrl,
  totalTasks: clickupData.length,
  listName: firstTask?.list?.name || 'Tasks',
  folderName: firstTask?.folder?.name || firstTask?.project?.name || 'Board',
  statusSummary: statusSummary,
  teamId: firstTask?.team_id || null
};

return { json: summary };

