// Get values from input item
let contractType = $input.first().json["Contract Type"];
let experienceLevel = $input.first().json["Experience Level"]; 
let workType = $input.first().json["Working Mode"];
let publishedAt = $input.first().json["Date Posted"];

// Mapping functions
function mapContractType(value) {
  if (!value) return null;
  switch (value) {
    case "Full-time": return "F";
    case "Part-time": return "P";
    case "Contract": return "C";
    case "Temporary": return "T";
    case "Internship": return "I";
    case "Volunteer": return "V";
    default: return null;
  }
}

function mapExperienceLevel(value) {
  if (!value) return null;
  switch (value) {
    case "Internship": return "1";
    case "Entry-level": return "2";
    case "Associate": return "3";
    case "Mid-senior level": return "4";
    case "Director": return "5";
    default: return null;
  }
}

function mapWorkType(value) {
  if (!value) return null;
  switch (value) {
    case "On-site": return "1";
    case "Remote": return "2";
    case "Hybrid": return "3";
    default: return null;
  }
}

function mapPublishedAt(value) {
  if (!value) return null;
  switch (value) {
    case "Past 24 hours": return "r86400";
    case "Past Week": return "r604800";
    case "Past Month": return "r2592000";
    default: return null;
  }
}

// Map all to codes
contractType = mapContractType(contractType);
experienceLevel = mapExperienceLevel(experienceLevel);
workType = mapWorkType(workType);
publishedAt = mapPublishedAt(publishedAt);

const rowMap = {
    r86400: 100,
    r604800: 300,
    r2592000: 600,
};

// Build output, excluding nulls
const output = {
  title: $input.first().json.title,
  location: $input.first().json.location,
  rows: rowMap[publishedAt] || 100,
};

if (contractType !== null) output.contractType = contractType;
if (experienceLevel !== null) output.experienceLevel = experienceLevel;
if (workType !== null) output.workType = workType;
if (publishedAt !== null) output.publishedAt = publishedAt;

return [{ json: output }];

// Get all GPT output items
const gptOutput = $input.all().map(item => item.json);
const targetScore = $('On form submission').first().json["Target Relevancy Score(out of 100)"];

// Extract jobs, filter by relevancy_score, and format
const filtered = gptOutput
  .map(entry => entry.message?.content)
  .filter(job => job && job.relevancy_score >= targetScore)
  .map(job => ({
    json: {
      id: job.id,
      relevancy_score: job.relevancy_score
    }
  }));

return filtered;

const blacklisted_companies = ["Jobot", "TieTalent", "Pryor Associates Executive Search"];

const jobs = $input.all().map(item => item.json);

// Deduplicate by id
const uniqueJobs = Array.from(
  new Map(jobs.map(job => [job.id, job])).values()
);

// Filter out blacklisted companies and jobs without companyUrl
const filteredJobs = uniqueJobs.filter(
  job => job.companyUrl && !blacklisted_companies.includes(job.companyName)
);

// Deduplicate by title + companyName (keep first only)
const seen = new Set();
const finalJobs = filteredJobs.filter(job => {
  const key = job.title + "|" + job.companyName;
  if (seen.has(key)) return false;  // skip duplicates
  seen.add(key);
  return true;  // keep first
});

return finalJobs.map(job => ({ json: job }));

