// Get the record from Airtable List
const record = $input.all()[0].json;

return [{
  json: {
    id: record.id
  }
}];

// Get ALL items (each day is a separate item)
const allDays = $input.all();
let delivered = 0;
let unique_opens = 0;
let unique_clicks = 0;

// Loop through each day (each item)
allDays.forEach(item => {
  const day = item.json;
  delivered += day.stats[0].metrics.delivered;
  unique_opens += day.stats[0].metrics.unique_opens;
  unique_clicks += day.stats[0].metrics.unique_clicks;
});

// Calculate rates from totals
const open_rate = delivered > 0 ? unique_opens / delivered : 0;
const ctr = delivered > 0 ? unique_clicks / delivered : 0;

return [{
  json: {
    week_ending: new Date().toISOString(),  // ‚Üê Changed this line
    delivered: delivered,
    unique_opens: unique_opens,
    unique_clicks: unique_clicks,
    open_rate: open_rate,
    ctr: ctr
  }
}];

// Just combine the objects cleanly
const stats = $input.all()[0].json;
const recordInfo = $input.all()[1].json;

return [{
  json: {
    id: recordInfo.id,
    ...stats  // Spread all stats fields
  }
}];

// Get the GPT response (should be just one item now)
const gptResponse = $input.first().json;

// Parse the JSON content from the message
const analysis = JSON.parse(gptResponse.message.content);

// Get the most recent week_ending from your previous data
// This assumes you have access to the original data from Search records1
// You may need to pass this through from an earlier node
const previousData = $('Search records1').first().json;
const lastWeekEnding = previousData.week_ending;

// Calculate next week's date (7 days after the most recent week_ending)
const nextWeekDate = new Date(lastWeekEnding);
nextWeekDate.setDate(nextWeekDate.getDate() + 7);

// Format the date as full ISO string for Airtable
const weekEnding = nextWeekDate.toISOString();

// Return formatted data for Airtable
return [{
  json: {
    week_ending: weekEnding,
    decision: analysis.decision,
    test_variable: analysis.test_variable,
    test_hypothesis: analysis.test_hypothesis,
    confidence_level: analysis.confidence_level,
    test_directive: analysis.test_directive,
    implementation_instruction: analysis.implementation_instruction,
    baseline_performance: analysis.baseline_performance,
    success_metric: analysis.success_metric,
    target_improvement: analysis.target_improvement,
    analysis: analysis.analysis,
    // Initialize performance metrics as 0 for the upcoming week
    delivered: 0,
    unique_opens: 0,
    unique_clicks: 0,
    open_rate: 0,
    ctr: 0,
    performance_delta: 0
  }
}];
