const items = $input.all();
return items.map(item => {
  const email = item.json['E-mail'];
  const domain = email ? email.split('@')[1] : null;

  return {
    json: {
      websiteDomain: domain ? 'https:' + '//' + domain : null  // Break up the protocol
    }
  };
});

// Flag items based on common email providers
const commonProviders = [
  'gmail', 'yahoo', 'ymail', 'rocketmail',
  'outlook', 'hotmail', 'live', 'msn',
  'icloud', 'me', 'mac', 'aol',
  'zoho', 'protonmail', 'mail', 'gmx'
];

const websiteDomain = $input.item.json.websiteDomain;
const protocol = 'https:' + '//';  // Break up the protocol
const domain = websiteDomain ? websiteDomain.replace(protocol, '') : '';
const domainParts = domain.split('.');
const mainDomain = domainParts[0].toLowerCase();
const isCommonProvider = commonProviders.includes(mainDomain);

return {
  json: {
    ...$input.item.json,
    isPersonalEmail: isCommonProvider
  }
};

