const chatInput = $json.chatInput ? $json.chatInput.trim() : '';

if (!chatInput) {
  return [{json: {error: 'No input provided', validationPassed: false}}];
}

const parts = chatInput.split(',').map(p => p.trim());
let email, name;

if (parts.length === 2) {
  email = parts[0].toLowerCase().replace(/['"]/g, '');
  name = parts[1].replace(/['"]/g, '');
} else if (parts.length === 1) {
  email = parts[0].toLowerCase().replace(/['"]/g, '');
  name = null;
} else {
  return [{json: {error: 'Invalid format. Use: email or email, name', validationPassed: false}}];
}

const emailRegex = new RegExp('^[^\\s@]+@[^\\s@]+\\.[^\\s@]+$');
if (!emailRegex.test(email)) {
  return [{json: {error: 'Invalid email format: ' + email, validationPassed: false}}];
}

const domain = email.split('@')[1];
const isFreeEmail = ['gmail.com', 'yahoo.com', 'hotmail.com', 'outlook.com'].includes(domain);

return [{
  json: {
    email: email,
    name: name,
    domain: domain,
    isFreeEmail: isFreeEmail,
    originalInput: chatInput,
    timestamp: new Date().toISOString(),
    validationPassed: true
  }
}];

const inputs = $input.all();

const enrichedData = {
  contact: {
    email: null,
    name: null
  },
  enrichment: {
    pdl: {},
    individual: {},
    company: {},
    linkedin: {}
  },
  metadata: {
    enrichmentTimestamp: new Date().toISOString(),
    sourcesSuccessful: [],
    sourcesFailed: []
  }
};

inputs.forEach((input, index) => {
  const data = input.json;

  if (index === 0 && data.choices && data.choices[0] && data.choices[0].message && data.choices[0].message.content) {
    const content = data.choices[0].message.content;
    enrichedData.enrichment.individual = {
      rawResearch: content,
      recentActivity: extractSection(content, 'RECENT ACTIVITY'),
      expertise: extractSection(content, 'EXPERTISE'),
      outreachAngle: extractSection(content, 'OUTREACH ANGLE'),
      timing: extractSection(content, 'TIMING')
    };
    enrichedData.metadata.sourcesSuccessful.push('Individual Research');
  }

  else if (index === 1 && data && !data.error) {
    const pdl = data.data || data;
    enrichedData.enrichment.pdl = {
      fullName: pdl.full_name,
      jobTitle: pdl.job_title,
      companyName: pdl.job_company_name,
      companySize: pdl.job_company_size,
      industry: pdl.job_company_industry,
      seniorityLevel: pdl.job_title_levels,
      linkedinUrl: pdl.linkedin_url,
      location: pdl.location_name,
      skills: pdl.skills
    };
    enrichedData.contact.name = pdl.full_name;
    enrichedData.metadata.sourcesSuccessful.push('PDL');
  }

  else if (index === 2 && data.choices && data.choices[0] && data.choices[0].message && data.choices[0].message.content) {
    const content = data.choices[0].message.content;
    enrichedData.enrichment.company = {
      rawResearch: content,
      developments: extractSection(content, 'RECENT DEVELOPMENTS'),
      buyingSignals: extractSection(content, 'BUYING SIGNALS'),
      companyHealth: extractSection(content, 'COMPANY HEALTH'),
      priority: extractSection(content, 'PRIORITY')
    };
    enrichedData.metadata.sourcesSuccessful.push('Company Research');
  }

  else if (index === 3 && data && Array.isArray(data) && data.length > 0) {
    const profile = data[0];
    enrichedData.enrichment.linkedin = {
      headline: profile.headline,
      summary: profile.summary,
      recentPosts: profile.posts ? profile.posts.slice(0, 5) : [],
      connectionsCount: profile.connectionsCount,
      postsAnalyzed: true
    };
    enrichedData.metadata.sourcesSuccessful.push('LinkedIn');
  }

  else if (index === 4 && data.validationPassed) {
    enrichedData.contact.email = data.email;
    enrichedData.contact.domain = data.domain;
    enrichedData.contact.isFreeEmail = data.isFreeEmail;
  }
});

const qualityScore = calculateQuality(enrichedData);
enrichedData.metadata.dataQualityScore = qualityScore;

function extractSection(text, header) {
  const pattern = header + ': ';
  const startIdx = text.indexOf(pattern);
  if (startIdx === -1) return 'N/A';
  const afterHeader = text.substring(startIdx + pattern.length);
  const endIdx = afterHeader.search(/\n[A-Z]/);
  return endIdx === -1 ? afterHeader.trim() : afterHeader.substring(0, endIdx).trim();
}

function calculateQuality(data) {
  let score = 0;
  if (data.enrichment.pdl.fullName) score += 25;
  if (data.enrichment.individual.recentActivity !== 'N/A') score += 25;
  if (data.enrichment.company.developments !== 'N/A') score += 25;
  if (data.enrichment.linkedin.postsAnalyzed) score += 25;
  return score;
}

return [{ json: enrichedData }];

const raw = $input.first().json.output;

let scoring;
try {
  let cleaned = raw.replace(/```json\n?|```\n?/g, '').trim();
  cleaned = cleaned.replace(/,(\s*[}\]])/g, '$1');
  scoring = JSON.parse(cleaned);

} catch (err) {
  const extract = (field) => {
    const regex = new RegExp('"' + field + '":\\s*"([^"]*(?:\\\\.[^"]*)*)"', 's');
    const match = raw.match(regex);
    return match ? match[1].replace(/\\n/g, '\n').replace(/\\"/g, '"') : null;
  };

  const extractArray = (field) => {
    const regex = new RegExp('"' + field + '":\\s*\\[([^\\]]+)\\]', 's');
    const match = raw.match(regex);
    if (!match) return [];
    return match[1].split(',').map(s => s.trim().replace(/^"|"$/g, ''));
  };

  const extractNumber = (field) => {
    const regex = new RegExp('"' + field + '":\\s*(\\d+)');
    const match = raw.match(regex);
    return match ? parseInt(match[1]) : 0;
  };

  const extractObject = (field) => {
    const regex = new RegExp('"' + field + '":\\s*({[^}]+})');
    const match = raw.match(regex);
    if (!match) return {};
    try {
      return JSON.parse(match[1]);
    } catch {
      return {};
    }
  };

  scoring = {
    email: extract('email'),
    name: extract('name'),
    title: extract('title'),
    companyName: extract('companyName'),
    companySize: extract('companySize'),
    industry: extract('industry'),
    seniorityLevel: extractArray('seniorityLevel'),
    linkedinUrl: extract('linkedinUrl'),
    individualResearch: extract('individualResearch'),
    companyResearch: extract('companyResearch'),
    dataQualityScore: extractNumber('dataQualityScore'),
    leadScore: extractNumber('leadScore'),
    scoreBreakdown: extractObject('scoreBreakdown'),
    icpMatch: extractObject('icpMatch'),
    keyInsights: extractArray('keyInsights'),
    outreachRecommendation: extract('outreachRecommendation'),
    conversationStarters: extractArray('conversationStarters'),
    timingOpportunities: extractArray('timingOpportunities'),
    redFlags: extractArray('redFlags'),
    nextAction: extract('nextAction'),
    confidenceLevel: extract('confidenceLevel'),
    routingCategory: extract('routingCategory')
  };
}

const finalLead = {
  email: scoring.email,
  name: scoring.name,
  title: scoring.title,
  companyName: scoring.companyName,
  companySize: scoring.companySize,
  industry: scoring.industry,
  seniorityLevel: scoring.seniorityLevel,
  linkedinUrl: scoring.linkedinUrl,
  individualResearch: scoring.individualResearch,
  companyResearch: scoring.companyResearch,
  dataQualityScore: scoring.dataQualityScore,
  leadScore: scoring.leadScore,
  scoreBreakdown: scoring.scoreBreakdown,
  scoreReasoning: scoring.leadScore + '/10 - ' + Object.entries(scoring.scoreBreakdown || {}).map(function(pair) { return pair[0] + ': ' + pair[1]; }).join(', '),
  icpMatch: scoring.icpMatch,
  companySizeMatch: scoring.icpMatch ? scoring.icpMatch.companySizeMatch : null,
  industryMatch: scoring.icpMatch ? scoring.icpMatch.industryMatch : null,
  titleMatch: scoring.icpMatch ? scoring.icpMatch.titleMatch : null,
  keyInsights: scoring.keyInsights || [],
  outreachRecommendation: scoring.outreachRecommendation,
  conversationStarters: scoring.conversationStarters || [],
  timingOpportunities: scoring.timingOpportunities || [],
  redFlags: scoring.redFlags || [],
  nextAction: scoring.nextAction,
  confidenceLevel: scoring.confidenceLevel,
  routingCategory: scoring.routingCategory,
  processedAt: new Date().toISOString()
};

return [{ json: finalLead }];

const inputData = $input.first().json;

const raw = inputData.content && inputData.content.parts && inputData.content.parts[0] ? inputData.content.parts[0].text : null;

if (!raw) {
  return [{json: {error: 'Could not find email content in response'}}];
}

const cleanedRaw = raw.replace(/```json\n?|```\n?/g, '').trim();
const emailData = JSON.parse(cleanedRaw);

return [{
  json: {
    to: emailData.email,
    subject: emailData.subject,
    body: emailData.body
  }
}];
