const items = $input.all();
const results = [];

for (const item of items) {
  const attachments = item.json.attachments || [];

  // Filter for PDF and image attachments (common invoice formats)
  const invoiceAttachments = attachments.filter(att => {
    const name = att.filename?.toLowerCase() || '';
    return name.endsWith('.pdf') || 
           name.endsWith('.png') || 
           name.endsWith('.jpg') || 
           name.endsWith('.jpeg');
  });

  // Process each invoice attachment
  for (const attachment of invoiceAttachments) {
    results.push({
      json: {
        email_from: item.json.from,
        email_subject: item.json.subject,
        email_date: item.json.date,
        email_id: item.json.id,
        attachment_name: attachment.filename,
        attachment_id: attachment.attachmentId,
        attachment_size: attachment.size,
        invoice_id: `INV-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`
      },
      binary: {
        data: item.binary[attachment.attachmentId]
      }
    });
  }
}

return results;

const items = $input.all();
const results = [];

for (const item of items) {
  try {
    // Parse AI response
    const aiText = item.json.message?.content || item.json.output || '{}';

    // Clean JSON
    let cleanJson = aiText.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();
    const jsonMatch = cleanJson.match(/\{[\s\S]*\}/);
    if (jsonMatch) {
      cleanJson = jsonMatch[0];
    }

    const invoiceData = JSON.parse(cleanJson);

    // Get original email data
    const originalData = $('Extract Invoice Attachments').item(0).json;

    results.push({
      json: {
        ...originalData,
        ...invoiceData,
        parsed_at: new Date().toISOString(),
        processing_status: 'extracted'
      }
    });
  } catch (error) {
    results.push({
      json: {
        ...item.json,
        parsing_error: error.message,
        processing_status: 'failed'
      }
    });
  }
}

return results;

