{
  "type": "object",
  "required": [
    "risk_level",
    "risk_score",
    "red_flags",
    "vendor_trust_score",
    "amount_validation",
    "document_quality_score",
    "duplicate_risk",
    "recommended_action",
    "required_verifications",
    "approval_workflow",
    "priority_level",
    "detailed_analysis_markdown"
  ],
  "properties": {
    "risk_level": {
      "type": "string",
      "enum": ["critical", "high", "medium", "low"]
    },
    "risk_score": {
      "type": "integer",
      "minimum": 0,
      "maximum": 100,
      "description": "Overall fraud risk score"
    },
    "red_flags": {
      "type": "array",
      "items": {"type": "string"},
      "description": "List of specific concerns found"
    },
    "vendor_trust_score": {
      "type": "integer",
      "minimum": 0,
      "maximum": 100
    },
    "amount_validation": {
      "type": "string",
      "description": "Is amount reasonable? Details"
    },
    "document_quality_score": {
      "type": "integer",
      "minimum": 0,
      "maximum": 100
    },
    "duplicate_risk": {
      "type": "string",
      "enum": ["none", "low", "medium", "high", "confirmed"]
    },
    "recommended_action": {
      "type": "string",
      "enum": ["auto_approve", "manager_review", "cfo_approval", "fraud_investigation", "reject"]
    },
    "required_verifications": {
      "type": "array",
      "items": {"type": "string"}
    },
    "approval_workflow": {
      "type": "string",
      "description": "Who should approve"
    },
    "priority_level": {
      "type": "string",
      "enum": ["urgent", "high", "normal", "low"]
    },
    "detailed_analysis_markdown": {
      "type": "string",
      "description": "Full analysis in markdown"
    }
  }
}

